// Print character
float _char(sampler2D s, vec2 u, int id) {
    vec2 p = vec2(id%16, 15. - floor(float(id)/16.));
         p = (u + p) / 16.;
         u = step(abs(u-.5), vec2(.5));
    return texture(s, p).x * u.x * u.y;
}

// Floating point debug
float _decimal(sampler2D FONT_TEXTURE, inout vec2 u, float n, int decimals) {
    float d = 0., N = 1.; // d is the final color, N the number of digits before the decimal

    if (n < 0.) {  // If the number is negative
        n *= -1.;  // Make it positive
        (0 _SUB ); // Print a minus sign
    }

    // Calculate the number of digits before the decimal point
    for (float x = n; x >= 10.; x /= 10.) N++;

    // Print the digits before the decimal point
    for (float i = 0.; i < N; i++) {        
        float magnitude = pow(10., N-i-1.);
        float leftDigit = floor(n / magnitude);
        n -= leftDigit * magnitude;

        (0 _dig(leftDigit) );
    }

    (0 _DOT ); // Print a dot

    // Print the digits after the decimal point
    for (int i = 0; i < decimals; i++) {
        float firstDecimal = floor((n - floor(n)) * 10.);
        n *= 10.;

        (0 _dig(firstDecimal) );
    }

    return d;
}

// keyboard: https://www.shadertoy.com/view/lsXGzf

const int KEY_LEFT  = 37;
const int KEY_UP    = 38;
const int KEY_RIGHT = 39;
const int KEY_DOWN  = 40;
const ivec2[] storeAddr = ivec2[]( P0_ADDR,P1_ADDR,P2_ADDR,P3_ADDR,P4_ADDR,P5_ADDR,P6_ADDR );

// --------------------------------------------------------

void initializeStorage(inout vec4 fragColor,vec2 fragCoord) {
    if (storeDataAddr(fragCoord, FOCUS_ADDR)) fragColor.xyz = vec3(0.);
    if (storeDataAddr(fragCoord, P0_ADDR)) fragColor.xyz = vec3(pData[ 0].start,pData[ 1].start,pData[ 2].start);
    if (storeDataAddr(fragCoord, P1_ADDR)) fragColor.xyz = vec3(pData[ 3].start,pData[ 4].start,pData[ 5].start);
    if (storeDataAddr(fragCoord, P2_ADDR)) fragColor.xyz = vec3(pData[ 6].start,pData[ 7].start,pData[ 8].start);
    if (storeDataAddr(fragCoord, P3_ADDR)) fragColor.xyz = vec3(pData[ 9].start,pData[10].start,pData[11].start);
    if (storeDataAddr(fragCoord, P4_ADDR)) fragColor.xyz = vec3(pData[12].start,pData[13].start,pData[14].start);
    if (storeDataAddr(fragCoord, P5_ADDR)) fragColor.xyz = vec3(pData[15].start,0.,0.);
}

bool keyClick(int ascii) {
	return (texture(iChannel1,vec2((.5+float(ascii))/256.,0.25)).x > 0.);
}

bool getKeyState(int keyCode, int stateType){
    return bool(texelFetch(iChannel1, ivec2(keyCode, stateType), 0).x);
}

// --------------------------------------------------------

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    if(fragCoord.y > 1.0) return; // skip unused storage locations

    if(iFrame < 10 || keyClick(32)) {  // reset settings on coldstart or <Spc> keypress
        initializeStorage(fragColor, fragCoord);
        return; 
    }

    bool resetDisplayCount = false;
    int focus = int(fetchData(iChannel0, FOCUS_ADDR).x);
    int focusIndex = focus / 3;  // vec3 index
    int focusField = focus % 3;  // x,y,z field within

    // always fetch current values so 'non changes' don't stomp on them 
    for(int i=0;i<6;++i)
        if (storeDataAddr(fragCoord, storeAddr[i])) 
            fragColor = fetchData(iChannel0, storeAddr[i]);

    // lt/rt arrow alters focused variable ---------------
    if (storeDataAddr(fragCoord, storeAddr[focusIndex])) {
        fragColor = fetchData(iChannel0, storeAddr[focusIndex]); // current value
        float changeAmount = 0.0;

        if(getKeyState(KEY_LEFT,0))  changeAmount = -pData[focus].delta; else
        if(getKeyState(KEY_RIGHT,0)) changeAmount = +pData[focus].delta;

        // mouse -------------------------------
        if(iMouse.z > 0.0) {
            vec2 mouseEffect = (iMouse.xy - iMouse.zw) * 0.01;
            changeAmount = mouseEffect.x * pData[focus].delta;
        }

        if(changeAmount != 0.)
            fragColor[focusField] = clamp(fragColor[focusField] + changeAmount,pData[focus].vmin,pData[focus].vmax);
    }   

    if(texelFetch( iChannel1, ivec2(KEY_LEFT,1),0 ).x > 0.0 ||
       texelFetch( iChannel1, ivec2(KEY_RIGHT,1),0 ).x > 0.0 ||
       iMouse.z > 0.0) resetDisplayCount = true;

    // alter focus.  update displayCount--------------------------
    if (storeDataAddr(fragCoord, FOCUS_ADDR)) {
        fragColor = fetchData(iChannel0, FOCUS_ADDR);

        float kym = texelFetch( iChannel1, ivec2(KEY_UP,1),0 ).x;
        float kyp = texelFetch( iChannel1, ivec2(KEY_DOWN,1),0 ).x;

        if(kym > 0.0) { fragColor.x -= 1.0; resetDisplayCount = true; } 
        if(kyp > 0.0) { fragColor.x += 1.0; resetDisplayCount = true; }

        const float maxIndex = 15.0;
        if(fragColor.x < 0.0) fragColor.x = maxIndex;
        if(fragColor.x > maxIndex) fragColor.x = 0.0;

        if(resetDisplayCount) fragColor.y = iFrameRate; else
        if(--fragColor.y < 0.0) fragColor.y = 0.0;  // so widget display times out 
    }   

}
