// 2D Random
float random (in vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))
                 * 43758.5453123);
}

#define PI 3.14159265358979323846

float box(vec2 _st, vec2 _size, float _smoothEdges){
    _size = vec2(0.5)-_size*0.5;
    vec2 aa = vec2(_smoothEdges*0.5);
    vec2 uv = smoothstep(_size, _size + aa, _st);
    uv *= smoothstep(_size, _size + aa, vec2(1.0) - _st);
    return uv.x * uv.y;
}

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

vec3 getGridColor(vec2 uv, vec2 translate, float timeOffset, vec2 size, float smoothEdges) {
    uv += translate * 0.9;
    vec2 localUV = fract(uv); // Get local UV within the grid cell
    localUV -= vec2(0.5);     // Move to center
    localUV = rotate2d(sin(iTime + timeOffset) * PI) * localUV; // Rotate locally
    localUV += vec2(0.5);     // Move back to original position

    vec3 blue = vec3(0.455,0.537,0.753);
    vec3 orange = vec3(0.961,0.318,0.035);
    vec3 offWhite = vec3(0.941,0.918,0.776);

    vec3 verGrad = mix(orange, blue, uv.y);
    vec3 horGrad = mix(offWhite, blue, uv.x);
    vec3 mixGrad = mix(mix(offWhite,blue, .1 + localUV.x * sin(iTime) + localUV.y * cos(iTime)), mix(verGrad, horGrad, -abs(sin(iTime + localUV.x) * .5)), localUV.x * localUV.y);    



    return (mixGrad * box(localUV, size, smoothEdges));
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;    


    vec2 pos = vec2(uv * 2.0);

    // Use the noise function
    float n = snoise(vec3(pos.x, pos.y, iTime * .5));


    vec3 col = vec3(0.0);
    vec2 translate1 = vec2(cos(iTime), sin(iTime));
    vec2 translate2 = vec2(sin(iTime), cos(iTime));
    vec2 translate3 = vec2(cos(iTime), cos(iTime));

    vec2 uv1 = uv * 10.0 + 0.5;
    col += getGridColor(uv1 * n, translate1, 0.0, vec2(0.25, 0.25), 0.01);

    vec2 uv2 = uv * 10.0 + 0.8;  // Adjusted initial UV for the second grid
    col += getGridColor(uv2 * n, translate2, 10.0, vec2(0.5, 0.5), 0.01);

    vec2 uv3 = uv * 10.0 - 0.3;  // Adjusted initial UV for the third grid
    col += getGridColor(uv3 * n, translate3, 15.0, vec2(0.75, 0.75), 0.01);


    // Output to screen
    fragColor = vec4(col, 1.0);
}
