const float V  = 0.15, // velocity
            WL = 0.06; // wavelength
const vec2[5] S = vec2[5]( // source positions
    vec2(-1.0, 0.2),
    vec2(-1.0, 0.1),
    vec2(-1.0, 0.0),
    vec2(-1.0, -0.1),
    vec2(-1.0, -0.2)
);

// courtesy of @berelium, thanks!
vec3 blackbody(float i) {
    float T = 1400.0 + 1400.0 * i; // temperature range (in Kelvin).
    vec3 L = vec3(7.4, 5.6, 4.4); // red, green, blue wavelengths (in hundreds of nanometers).
    L = pow(L, vec3(5.0)) * (exp(1.43876719683e5 / (T * L)) - 1.0);
    return 1.0 - exp(-50e7 / L); // exposure level
}

void mainImage(out vec4 o, in vec2 uv) {
    // square and center coordinates
    vec2 res = iResolution.xy,
         pos = (2.0 * uv - res) / res.y;

    // distances and amplitudes
    float d,
          a = 0.0;
    for (int i = 0; i < S.length() ; i++) {
        d = length(S[i] - pos);
        a += sin(3.1415 * (d - iTime * V) / WL) / d;
    }

    // use blackbody with some scaling on the amplitude, and a gamma correction
    float s = pow(sin(iTime * 0.5) * 2.0 + 4.0, 2.0);
    vec3 col = blackbody(a / s);
    col = pow(col, vec3(1.0 / 2.2));
    o = vec4(col, 1.0);
}
