vec3 sdfColor(float sdf)
{
    return vec3(sdf);
}

float sdfCircle(vec2 point, float radius)
{
    return length(point) - radius;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uResolution =iResolution.xy;
    float uTime = iTime;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = gl_FragCoord.xy/uResolution.xy;


    uv = uv - 0.5;
    uv = uv * uResolution/100.;

    float radius = uResolution.x/500.;
    //moving back and forth horizontally
    //vec2 center = vec2(sin(2.0*uTime),0.0);

    vec2 center = vec2(0.0);

    float sdf = sdfCircle(uv - center, radius);

    //solid color circle
    vec3 color = sdf > 0.0? vec3(0.9,0.6,0.0): vec3(0.3,0.7,1.0);

    //sun effect
    //color = color * exp(sdf);

    //black outline
    color = color * (1.0 - exp(-2.0 *abs(sdf)));

    //white outline
    //color = mix(vec3(1.0), color, step(0.1,abs(sdf)));

    color = mix(vec3(1.0), color, smoothstep(0.0,0.1,abs(sdf)));

    //gradient circle
    //color = mix(color,sdfColor(sdf),0.9);


    // Output to screen
    //vec3 color = vec3(uv.x,uv.y,0);
    fragColor = vec4(color,1.0);
}
