#define IMG_NUMS   4
#define SPEED      .5
#define PI         3.141592653589793
#define TAU        6.283185307179586

vec4 getPhoto(vec2 uv, int id) {
    if (id == 0) return texture(iChannel0, uv);
    if (id == 1) return texture(iChannel1, uv);
    if (id == 2) return texture(iChannel2, uv);
    if (id == 3) return texture(iChannel3, uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;
    float t = iTime*SPEED;

    vec2 noiseRG;
    float scal = 1., gain = 1., ofst = .5;
    vec3 move = vec3(0., t*2., t);
    noiseRG  =     1.*snoise3DImage((uv-vec2(421., 132))*1., scal, gain, ofst, move).rg;
    noiseRG +=     .5*snoise3DImage((uv-vec2(913., 687))*2., scal, gain, ofst, move).rg;
    noiseRG +=    .25*snoise3DImage((uv-vec2(834., 724))*4., scal, gain, ofst, move).rg;
    noiseRG +=   .125*snoise3DImage((uv-vec2(125., 209))*8., scal, gain, ofst, move).rg;
    noiseRG +=  .0625*snoise3DImage((uv-vec2(387., 99))*16., scal, gain, ofst, move).rg;
    noiseRG /= 1.9375;

    float wei = smoo3((.5+.5*cos((fract(t)-.5)*TAU)));
    uv = displace(uv, noiseRG, ofst, wei);

    int it = int(t);
    float grad = .5+.5*sin((fract(t)-.5)*PI);

    int currID =   it & IMG_NUMS-1;
    int nextID = it+1 & IMG_NUMS-1;
    vec4 currImg = getPhoto(uv, currID);
    vec4 nextImg = getPhoto(uv, nextID);

    fragColor = mix(currImg, nextImg, grad);
}

