vec4 snoise3DImage(vec2 uv, float scal, float gain, float ofst, vec3 move) {
    uv *= scal;
    float R = snoise(vec3(uv, 100.)+move);
    float G = snoise(vec3(uv, 300.)+move);
    float B = snoise(vec3(uv, 500.)+move);
    vec3 color = ofst+gain*vec3(R, G, B);
    return vec4(color, 1.);
}
