vec2 triangle_wave(vec2 a){
    vec2 a2 =
        vec2(1.,0.5)
    ,
    a1 = a+a2;
    return abs(fract((a1)*(a2.x+a2.y))-.5);
}

vec3 fractal(vec2 uv){
    vec3 col = vec3(0.);
    float scale = 1.5;
    vec2 t2 = vec2(0.);
    vec3 col1 = col;
    float c1=0.;
    for(int k = 0; k < 12; k++){

        uv.x += 1./4.;

        //uv = (uv+t2)/scale;
        uv = sign(uv.y-uv.x)*(uv+t2)/scale;

        uv = (fract(vec2(uv+vec2(.5,1.5))*scale)-.5)/scale;

        t2 =
            triangle_wave(uv+.5)

            //purple
            //triangle_wave(uv+.5) - 1.
        ;
        uv =
            //t2-triangle_wave(uv.yx)+fract(t2/2.)

            t2-triangle_wave(uv.yx)-fract(t2/2.)+vec2(floor(-uv.y),0.)
        ;
        //uv.x *= sign(uv.x+.5);
        c1 =
            //max(abs(uv.y-uv.x*sign(uv.x-uv.y))/2.,c1)
            max(abs(uv.y-uv.x)/2.,c1)
        ;
        c1 =
            1.-abs(2.*c1-1.)

            //more colorful
            //abs(2.*c1-1.)

            //Spooky!
            //max(abs(2.*c1-2.),c1/4.)/2.
        ;
        col.x =
             //col.x

            //blue and white
            //c1

            //red and blue and white
            //max(1.-abs(2.*c1-1.),c1/4.)


            //more colorful
            //max(length(uv-t2-c1)/3.,1.-col.x)

            //blue and green
            //max(length(uv-c1)/3.,col.x)
            length(uv-c1)/3.

            //purple
            //2. - max((abs(uv.y-uv.x))/3.,col.x)

            //black and white
            //col.x
        ;
        col =
            abs(col-(1.-(c1*col.x)));
        col1 =
            abs(col1*c1-col-1.).yzx
            //abs(col1*c1-col*sign(t2.y-t2.x)-1.).yzx
        ;
        //uv.y += sign(uv.x-.5)/2.;
        //uv.x += sign(uv.y-.5)/2.;
    }
    return col1;
}

