float mod1(float a, float b){
    return
        mod(a,b)
        //mod(a+mod(a,b+3.),b)
    ;
}

float fmod(float a, float b){
    //the modulus should be a prime number
    //this makes some interesting rhythms
    float modulus =
        //3.
        5.
        //7.
    ;
    for(float i = 1.; i < mod(floor(a/b),modulus); i++){
    //for(float i = 1.+mod(floor(a/b),3.); i < mod(floor(a/b),modulus); i++){
        a += mod1(floor(a/2.),b)*i;
    }
    return
        mod(floor(a),b)
    ;
}

