/**/ // -2 SnoopethDuckDuck
#define G max( min(  max( abs(u.x += .1)  ,                             \
                          abs(u.y+.5) + .11 + asin(cos(18.8*u.x))/19. ) \
                   , length(u)                                          \
                  ) -.5                                                 \
               , .15 - length(vec2(u.y, abs(u)-.2)) )

void mainImage( out vec4 O, vec2 u ) {
    O.xyz = iResolution;
    u = ( u+u - O.xy ) / O.y - .2;   
    O = 1. - O.y/2.* vec4(G,G,G,0);
}





