/** // --- 262 chars  -3 Fab

#define G(x) max( min( length(vec2(x,y)) ,                                   \
                        max( abs(x)  ,                                       \
                             abs(y+.5) +.2 -min(mod(x,.33),.33-mod(x,.33)) ) \
                      ) -.5,                                                 \
                 .15 - length(vec2(abs(x)-.2,y)) )

