// ShaderToy Shader code

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Get the normalized coordinates of the pixel
    vec2 uv = fragCoord.xy / iResolution.xy;

    // Use audio data from iChannel0 (music input) for visual effects
    // We're sampling the low, mid, and high frequencies
    float bass = texture(iChannel0, vec2(0.01, 0.0)).r; // Bass
    float mid = texture(iChannel0, vec2(0.1, 0.0)).r;  // Mid-range
    float treble = texture(iChannel0, vec2(0.3, 0.0)).r; // Treble

    // Create a time variable
    float time = iTime * 0.5;

    // Create a pulsing effect based on the bass frequency
    float pulse = 0.5 + 0.5 * sin(time * 5.0 * bass);

    // Generate a dynamic color that reacts to the bass and mid frequencies
    vec3 color = vec3(
        0.5 + 0.5 * sin(uv.x * 10.0 + time + bass),
        0.5 + 0.5 * cos(uv.y * 10.0 + time + mid),
        0.5 + 0.5 * sin(uv.x + uv.y + pulse)
    );

    // Amplify the effect with a bright pulse
    color *= pulse;

    // Output the final color to the screen
    fragColor = vec4(color, 1.0);
}
