// 2024.06.03 rev 1

#define ray_tmin 0.1
#define ray_tmax 100.0
#define samples 50
#define sample_offset 0.003

bool anti_alias = true;

// returns a back and forth 0.0 to 1.0 per second
float cycle(float perSecond) {
    float d = fract((iTime + iTimeDelta) * perSecond);

    // step 0.5 is used to reverse the keyframe, to get a coming and going effect
    // since the cut point is 0.5, we need to get original place (e.g. 1 again) if d is exactly to 0.5
    // this means that it cycles from 0...1...0 every second
    return 2. * ( d>.5 ? 1.-d : d );
}

// true / false per second
bool blink(float perSecond) {
    return cycle(perSecond / 2.) > 0.5;
}

// from https://www.reedbeta.com/blog/hash-functions-for-gpu-rendering/
uint global_hash = 0u;
float pcg_hash_float(uint x)
{
    uint state = x * 747796405u + 2891336453u + global_hash;
    uint word = ((state >> ((state >> 28u) + 4u)) ^ state) * 277803737u;
    uint hashed_value = (word >> 22u) ^ word;
    global_hash = hashed_value;
    return float(hashed_value) / 4294967295.0; // 4294967295 is 2^32 - 1
}

float pcg_hash_float(float x)
{
    return pcg_hash_float(floatBitsToUint(x));
}

// returns x value of a noise texture
float rand(int idx) {
    return texelFetch(iChannel0, ivec2(idx, 0), 0).x - 0.5;
}

// normal relative to ray direction
// e.g. if inside a sphere it returns -normal
bool front_face(vec3 d, vec3 out_n, out vec3 n) {
    bool outside = dot(d, out_n) < 0.;
    n = outside ? n : -n;
    return outside;
}

// returns t for a vector
// o - origin of the vector
// dir - direction of the vector
// t - can be read as "time"
vec3 at(vec3 o, vec3 dir, float t) {
    return o + t * dir;
}

// length squared
// optimized function for dot(v, v)
float length_squared(vec3 v) {
    return v.x * v.x + v.y * v.y + v.z * v.z;
}

// from Ray Tracing in One Weekend
// coord - sphere coord
// radius - sphere radius
// o - ray origin
// d - ray direction
// t - distance from o to sphere collision
// p - point of the collision
// n - normal of the collision (not normalized)
// outside - whether the hit was inside or outside the sphere
bool hit_sphere(
    vec3 coord,
    float radius,
    vec3 o,
    vec3 d,
    out float t,
    out vec3 p,
    out vec3 n,
    out bool outside
    ) {
    vec3 oc = coord - o;
    float a = length_squared(d);
    float b = dot(d, oc);
    float c = length_squared(oc) - radius * radius;
    float discriminant = b * b - a * c;
    if (discriminant < 0.) {
        t = -1.0;
        return false;
    }
    float sqrtd = sqrt(discriminant);

    float root = (b - sqrtd) / a;
    if (root <= ray_tmin || ray_tmax <= root) {
        root = (b + sqrtd) / a;
        if (root <= ray_tmin || ray_tmax <= root) {
            return false;
        }
    }

    t = root;
    p = at(o, d, t);
    n = (p - coord) / radius;
    outside = front_face(d, n, n);
    return true;
}

// returns a range of vec2(-1) to vec2(1) based on mouse and screen size
// deals with first frame and returns vec2(0) if so
vec2 uv_mouse() {
    if (length(iMouse.xy) == 0.) {
        return vec2(0, 0);
    }
    return ((iMouse.xy / iResolution.xy) - 0.5) * 2.;
}

vec3 get_ray(vec2 uv) {
    if (!anti_alias) {
        return vec3(uv, -1.);
    }
    float seed = uv.y * uv.x;
    vec2 offset = vec2(
                        pcg_hash_float(seed) - 0.5,
                        pcg_hash_float(seed) - 0.5
                        );
    return vec3(uv + offset * sample_offset, -1.);
}

// function that returns the color of a sphere
bool sphere(vec3 cam, vec3 dir, vec3 pos, float radius, out vec4 hit) {
    float t;
    vec3 p;
    vec3 n;
    bool outside;
    bool is_hit = hit_sphere(pos, radius, cam, dir, t, p, n, outside);
    if (!is_hit) {
        return is_hit;
    }

    vec3 c = vec3(0.865, 0.13, 0.32);
    vec3 N = normalize(n);
    if (!outside) {
        c = vec3(0., 0., 1.);
    }

    hit = vec4(mix(c, vec3(0.), min(1., t / 2.)), t);
    return is_hit;
}

void mainImage(out vec4 frag_color, in vec2 frag_coord) {
    anti_alias = blink(0.5);
    vec2 uv = (2. * frag_coord - iResolution.xy) / iResolution.y;
    vec3 origin = vec3(0.);
    vec4 O = vec4(0., 0., 0., 1.);
    vec2 mouse = uv_mouse();

    // world space, x=horizontal, y=vertical, z=depth
    vec3 cam_w = vec3(-mouse, 0.);

    // move everything to camera/view space
    vec3 cam_c = origin;
    // camera always points 
    vec3 cam_dir = vec3(0., 0., -1.);

    // we already have the NDC, which is uv from -1 to 1 at xy
    // we just place a plane at the front of the camera here
    // get the direction from camera to the uv plane
    vec3 dir = normalize(vec3(uv, -1.));

    // from Ray Tracing in One Weekend
    // blue background
    float t = 0.5 * (dir.y + 1.0);
    t = 0.5 * (dir.y + 1.0);
    vec4 bg_color = vec4((1.0 - t) * vec3(1.0, 1.0, 1.0) + t * vec3(0.5, 0.7, 1.0), 1.);

    int to_sample = anti_alias ? samples:1;
    float sample_scale = anti_alias ? 1.0/float(samples):1.;
    for (int cur_sample = 0; cur_sample < to_sample; cur_sample++) {
        // get random sample direction
        dir = get_ray(uv);

        vec4 hit = vec4(0., 0., 0., ray_tmax);
        bool sphere_hit = false;
        for (int i = 0; i < 20; i++) {
            // randomize the sphere position
            float radius = abs(rand(i + 3)) * 1. + 0.2;
            float sphere_y = mod((rand(i + 1) * 1.), 8.);
            vec3 pos_w = vec3(rand(i) * 10., sphere_y, -0.75 -rand(i + 2));

            // move sphere to camera space
            vec3 pos_c = pos_w - cam_w;

            // calculate a sphere hit with the current sample
            // get the closes sphere possible
            vec4 new_hit;
            bool is_hit = sphere(cam_c, dir, pos_c, radius, new_hit);
            sphere_hit = sphere_hit || is_hit;
            hit = is_hit && new_hit.w < hit.w ? new_hit : hit;
        }

        if (sphere_hit) {
            O += vec4(hit.xyz, 0.);
        } else {
            O += vec4(bg_color.xyz, 0.);
        }
    }

    frag_color = vec4(O.xyz*sample_scale, 1.);

    //frag_color = vec4(vec3(pcg_hash_float(floatBitsToUint(iTime + uv.y * uv.x))), 1.);
}
