float rand(vec2 p) {
    return fract(sin(dot(p, vec2(12.99, 78.233))) * 43758.545);
}

float noise(vec2 p) {
    vec2 f = fract(p);
    f = f * f * f * f * (3. - 2. * f) * (3. - 2. * f);
    vec2 i = floor(p);
    return mix(mix(rand(i + vec2(0, 0)), 
                   rand(i + vec2(1, 0)), f.x),
               mix(rand(i + vec2(0, 1)), 
                   rand(i + vec2(1, 1)), f.x), f.y);
}

float fbm(vec2 p) {
    float v = 0.;
    float a = 1.;
    for(int i = 0; i < 4; ++i) {
        p = 1.5 * p + 15.;
        a *= 0.5;
        v += a * noise(p);
    }
    return v;
}

#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)(cos((h)*6.3+vec3(0,23,21))*5.5+.5)
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 p = 2. * fragCoord.xy / iResolution.y;
    vec4 O=fragColor;
    vec2 C = fragCoord;
     O=vec4(0);

     vec2 r1 = vec2(fbm(p + 0.02 * iTime), fbm(p + 0.005 * iTime));
    vec2 r2 = vec2(fbm(p + 0.15 * iTime + 10. * r1), fbm(p + 0.12 * iTime + 12. * r1));

    fragColor = vec4(1.8 * pow(fbm(p + r2), 2.)  + 0.03);

    vec3 n1,q,r=iResolution,
    d=normalize(vec3((C*2.-r.xy)/r.y,1));  
    for(float i=0.,a,s,e,g=0.;
        ++i<70.;
        O.xyz+=mix(vec3(1),H(g*.1),.8)*1./e/8e3
    )
    {
        n1=g*d;
        n1.z+=10.*iTime;
n1.xy*=r1;

        a=50.;
        n1=mod(n1-a,a*2.)-a;
        s=2.;
        n1.xy*=r1;

        for(int i=0;i++<8;){
            n1=.15-abs(n1);
            n1.xy+=r2;
            n1.x<n1.z?n1=n1.zyx:n1;
            n1.z<n1.y?n1=n1.xzy:n1;
            s*=e=1.1+sin(iTime*.01)*.1;
            n1=abs(n1)*e-
                vec3(
                    5.*3.,
                    120,
                    8.*5.
                 );
         }
         g+=e=length(n1.yzz)/s;
    }
    fragColor=O;
}
