vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}
mat2 rot2D(float a) {
    return mat2(cos(a), -sin(a), sin(a), cos(a));
}
float smin( float a, float b, float k )
{
    //k *= 6.0;
    float h = max( k-abs(a-b), 0.0 )/k;
    return min(a,b) - h*h*h*k*(1.0/6.0);
}

float sdOctahedron(vec3 p, float s) {
    p = abs(p);
    return (p.x+p.y+p.z-s)*0.57735027;
}

float sdSphere(vec3 p, float s) {
    return length(p) - s;
}

float map(vec3 p) {


    p.z += iTime * 0.4;
    p.xy = (fract(p.xy) - .5);
    p.z = mod(p.z, .25) - 0.125;

    //q.xy *= rot2D(iTime);

    float box = sdOctahedron(p, 0.15);


    return box;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 m = (iMouse.xy * 2.0 - iResolution.xy) / iResolution.y;
    if (iMouse.z <= 0.) m = vec2(cos(iTime*.2), sin(iTime*.2));
    vec3 diffuse = vec3(0.0);

    vec3 ro = vec3(0, 0, -3);
    vec3 rd = normalize(vec3(uv * 1.5, 1));
    float t = 0.;

    // ro.yz *= rot2D(-m.y);
    // rd.yz *= rot2D(-m.y);

    // ro.xz *= rot2D(-m.x);
    // rd.xz *= rot2D(-m.x);

    int i;
    for (i = 0; i < 80; i++) {
        vec3 p = ro + rd * t;
        p.xy *= rot2D(t * 0.2);
        p.y += sin(t * (m.y + 1.) * 0.5) * 0.35;
        p.z = sin(p.z * t);
        // p.x += cos(t);
        float d = map(p);

        t += d;

        if (d < 0.001 || t > 100.) break;
    }

    diffuse = pal( t*0.04 + float(i) * 0.005,vec3(0.638, -2.782, 0.428),vec3(-0.443, 2.103, 0.335),vec3(0.750, 0.750, 0.750),vec3(-3.032, -2.698, -2.365) );
    // diffuse = vec3(float(i)/80.);

    fragColor = vec4(diffuse, 1.0);
}
