#define R(p,a,r) mix(a*dot(p,a),p,cos(r)) + sin(r)*cross(p,a)
#define H(h) (cos((h)*2.3+vec3(0,23,51))*1.5+.5)

vec3 color(float t) {
  vec3 c1 = vec3(1.0, 0.0, 0.0);
  vec3 c2 = vec3(0.0, 1.0, 0.0);
  vec3 c3 = vec3(0.0, 0.0, 1.0);
  vec3 c4 = vec3(1.0, 1.0, 0.0);
  vec3 c5 = vec3(1.0, 0.0, 1.0);
  vec3 c6 = vec3(0.0, 1.0, 1.0);

  float r = cos(t) * 0.5 + 0.5;
  if (r < 0.23) return mix(c1, c2, r * 5.0);
  if (r < 0.44) return mix(c2, c3, (r - 0.2) * 5.0);
  if (r < 0.65) return mix(c3, c4, (r - 0.4) * 5.0);
  if (r < 0.86) return mix(c4, c5, (r - 0.6) * 5.0);
  return mix(c5, c6, (r - 0.8) * 7.0);
}
vec2 hash22(vec2 p)
{
  vec3 p3 = fract(vec3(p.xyx) * vec3(1.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

void mainImage(out vec4 fragColor, vec2 fragCoord)
{
    fragColor = vec4(0);
      vec2 uv = fragCoord/iResolution.xy;
    vec3 p, r = iResolution, d = normalize(vec3((fragCoord-.5*r.xy)/r.y,0.7));  
    for(float i = 0., g = 0., e, s; i < 99.; ++i)
    {
                vec2 c22 =   hash22(uv);

        p = g *c22.x* tan(d);

        p.z -= 0.975;


        p = R(p, normalize(vec3(50,12,50)), iTime);
        s = 5.;


        for(int j = 0; j++ < 10;)
        {
         p = R(p, normalize(vec3(1,1,1)), iTime*0.1);
            p = abs(p);

            p = p.x < p.y ? p.zxy : p.yxz;

            s *= e = 1.7 / min(dot(p, p), 1.31);
            p = p * e - vec3(10,5,5);

        }

        g += e = length(p.xy*p.zy) / s;
        fragColor.rgb += color(iTime * 0.01 + cos(i)) * mix(r / r, H(log(s)), 0.5) * 0.08 * exp(-i * i * e);
    }

    fragColor = pow(fragColor, vec4(3));
}
