void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    // M.I.T. License, use however you see fit.
    vec2 uv = fragCoord/iResolution.xy;

    // My idea is to have an animated rasterbar that bounces up and down with sinus acceleration
    vec3 col = vec3(0.0, 0.0, 0.0);
    float threshold = 0.5 + 0.38*sin(iTime*2.0);
    if(uv.y > threshold-0.12 && uv.y < threshold + 0.12) {
        col = vec3(0.7, 0, 0.7);
    }

    // Output to screen
    fragColor = vec4(col, 1.0);
}
