vec3 palette(float t) {
    vec3 a = vec3(1.000,1.000,1.000);
    vec3 b = vec3(1.000,0.000,0.584);
    vec3 c = vec3(1.000,0.000,0.682);
    vec3 d = vec3(0.000,0.431,1.000);

    return a + b*cos(6.28318*(c*t+d) );
}

float easeInOutQuad(float t) {
    return t < 0.5 ? 2.0 * t * t : 1.0 - pow(-2.0 * t + 2.0, 2.0) / 2.0;
}

float loopTime(float t) {
    float loopDuration = 12.; // 6 seconds forward, 6 seconds backward
    float halfLoop = loopDuration / 2.0;
    float loopedTime = mod(t, loopDuration);

    if (loopedTime < halfLoop) {
        float normalizedTime = loopedTime / halfLoop;
        return easeInOutQuad(normalizedTime) * halfLoop;
    } else {
        float normalizedTime = (loopedTime - halfLoop) / halfLoop;
        return halfLoop - easeInOutQuad(normalizedTime) * halfLoop;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord - iResolution.xy ) / iResolution.y; 
    vec2 uv0 = uv;



    vec3 finalColor = vec3(0.0);

    float loopedTime = loopTime(iTime);

    for(float i = 0.0; i <7.0; i++){

        uv = fract(uv * .7) -0.5;
        float d = length(uv);
        vec3 col = palette(loopedTime/99.0 + length(uv0));
        d = sin(d*1.85 + loopedTime/4.5)/8.;
        d = abs(d);
        d = .03 / d;
        finalColor += col * d;

    }

    // Add vignette effect
    vec2 vignetteUV = fragCoord / iResolution.xy;
    vignetteUV = (vignetteUV - 0.5) * 2.0;
    float vignette = 1.0 - dot(vignetteUV, vignetteUV);
    vignette = pow(vignette, 1.5);

    finalColor *= vignette;

    // Apply smoothstep to the entire final color
    finalColor = .5+.5*(finalColor-1.) / fwidth(finalColor);

    fragColor = vec4(finalColor, 1.0);
}
