void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float tileSize1 = (sin(iTime/2.0)+2.0)*50.0;
    float tileSize2 = (sin(iTime/2.5)+2.0)*50.0;
    float tileSize3 = (sin(iTime/3.0)+2.0)*50.0;
    float tileSize4 = 150.0;
    float b1 = 0.0;
    float b2 = 0.0;
    float b3 = 0.0;
    float b4 = 0.0;
    vec2 pos1 = floor(fragCoord/tileSize1);
    vec2 pos2 = floor(fragCoord/tileSize2);
    vec2 pos3 = floor(fragCoord/tileSize3);
    vec2 pos4 = floor((fragCoord-iTime*20.0)/tileSize4);

    if (mod(pos1.x+pos1.y, 2.0) == 1.0) {
        b1 = 0.1;
    } else {
        b1 = 1.0;
    }

    if (mod(pos2.x+pos2.y, 2.0) == 1.0) {
        b2 = 0.5*sin(iTime);
    } else {
        b2 = 1.0;
    }
    if (mod(pos3.x+pos3.y, 2.0) == 1.0) {
        b3 = 0.5*cos(iTime);
    } else {
        b3 = 0.8;
    }
    if (mod(pos4.x+pos4.y, 2.0) == 1.0) {
        b4 = 0.0;
    } else {
        b4 = 1.0;
    }

    vec3 c1 = vec3(b1, 0, 0);
    vec3 c2 = vec3(0, 0, b2);
    vec3 c3 = vec3(0, b3, 0);
    vec3 c4 = vec3(b4, b4, b4)/(2.5+(0.5*sin(iTime)));
    fragColor = vec4(c4+(c1+c2+c3), 1.0);
}
