void mainImage( out vec4 O, vec2 U ){
    vec2 uv = U/r;
    vec2 cuv = (2.*U-r)/r.y;
    float t = mod(float(iFrame)/pieces, 1. + .5/pieces);

    O = vec4(0);
    vec2 A = texelFetch(iChannel0, ivec2(0,0), 0).xy;
    vec2 B = texelFetch(iChannel0, ivec2(0,1), 0).xy;
    vec2 C = texelFetch(iChannel0, ivec2(0,2), 0).xy;

    float x = 0.;
    x += .5/r.y/pow(length(A - cuv), 2.);
    x += .5/r.y/pow(length(B - cuv), 2.);
    x += .5/r.y/pow(length(C - cuv), 2.);

    O += vec4(x);

    x = texture(iChannel1, uv).w * sqrt(pieces);
    x*=x * 15.;
    O += vec4(x*x, x, sqrt(x), 1);

}

void mainImage( out vec4 O, vec2 U ){

    if (U.x < 1. && U.y < 4.){

        vec2 uv = (2.*U-r)/r.y;
        vec2 muv = (2.*iMouse.xy-r)/r.y;

        // w = following mouse? 
        // z = previous frame was mouse down?
        // prevent other dots from locking on when you drag over them

        if (iFrame < 10){
            if (U == vec2(.5)){
                O = vec4(-.9 * r.x/r.y, -.9, 0, 0);
                return;
            }

            if (U == vec2(.5, 1.5)){
                O = vec4(0, .9, 0, 0);
                return;
            }

            if (U == vec2(.5, 2.5)){
                O = vec4(.9 * r.x/r.y, -.9, 0, 0);
                return;
            }
        }

        O = texelFetch(iChannel0, ivec2(U), 0);

        if (length(O.xy - muv) < .1){
            if (iMouse.z > .5 && O.z < .5 && length(O.xy - muv) < .1){
                O.w = 1.;
            } 



            if (iMouse.z < .5 && O.z > .5){
                O.w = 0.;
            }

        }
        O.z = iMouse.z;

        if (O.w > .5){
            O.xy = muv;
        }

    } else {
        O = vec4(0);
    }
}

#define thing(x) min(1., max(3./r.y - x, 0.)*r.y/2.)

float line( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
} // By Inigo Quilez cuz I was too lazy to derive my own for this project
 // I already spent hours trying to derive a cubic bezier distance function ðŸ’€

void mainImage( out vec4 O, vec2 U ){
    vec2 uv = U/r;
    vec2 cuv = (2.*U-r)/r.y;
    float old, x = 0., t = mod(float(iFrame)
    #ifdef smooth
        * 11.639
    #endif
    /pieces, 1. + 1./pieces) - 1./pieces;
    old = iFrame < 20?0.:texture(iChannel1, uv).w;

    O = vec4(0);
    vec2 A = texelFetch(iChannel0, ivec2(0,0), 0).xy;
    vec2 B = texelFetch(iChannel0, ivec2(0,1), 0).xy;
    vec2 C = texelFetch(iChannel0, ivec2(0,2), 0).xy;

    vec2 E = mix(A, B, t);
    vec2 F = mix(B, C, t);

    vec2 P = mix(E, F, t);
    vec2 dPdt = -2.*A*(1.-t)+2.*B*(1.-2.*t) + 2.*C*t;

    #ifdef tangent
        x += thing(line(cuv, P-dPdt, P+dPdt));
    #else
        x += thing(line(cuv, E, F));
    #endif
    //x += 1e-3/pow(length(cuv - mix(E, F, t)), 2.);
    // if you enable this, remove line 19 in Image

    float f = (.1 + exp(-iTime*5.)) / pieces;
    O.w = min(1., old * (1.-f) / exp(old*.1) + x * f);

}

