// 5/7/24: Stability and speed improvements 
//         (Mostly from using more digits in the constants sqrt(2), sqrt(2)/2, and pi)
//         Go Fullscreen for more stability.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float v = texture(iChannel0, uv).x;
    float f = (1.0 - 1.0/(1.0 + abs(v))) * sign(v);

    v = texture(iChannel0, uv).w;
    float h = (1.0 - 1.0/(1.0 + abs(v))) * sign(v);

    // Linear renderer:
    //fragColor = (0.3*texture(iChannel0, uv).xxxx + vec4(0.0, 0.0, texture(iChannel0, uv).z, 0.0));

    // Inverse renderer (higher values approach but never reach full brightness):
    fragColor = vec4(abs(f), f, f, 1.0);

    //if (h > 0.491) {
        fragColor += vec4(0.0, h, 0.0, 0.0);
    //}
    fragColor.z += texture(iChannel0, uv).z*0.5;
}

// Use the Shadertoy Custom Textures extension to upload a custom heightmap into iChannel2.
// https://chrome.google.com/webstore/detail/jgeibpcndpjboeebilehgbpkopkgkjda


float lDistance(vec2 p0, vec2 p1, vec2 p2) {
    vec2 p01 = p0 - p1;
    vec2 p21 = p2 - p1;
    float dot = p01.x * p21.x + p01.y * p21.y;
    float len_sq = p21.x * p21.x + p21.y * p21.y;
    float param = -1.0;
    if (len_sq != 0.0) param = dot / len_sq;

    vec2 p = p1 + param * p21;

    if (param < 0.0) p = p1;
    else if (param > 1.0) p = p2;

    vec2 d = p0 - p;
    return length(d);
}
vec2[] dss = vec2[] (
vec2(1.0, 0.0),
vec2(0.70710678118, 0.70710678118),
vec2(0.0, 1.0),
vec2(-0.70710678118, 0.70710678118),
vec2(-1.0, 0.0),
vec2(-0.70710678118, -0.70710678118),
vec2(0.0, -1.0),
vec2(0.70710678118, -0.70710678118),
vec2(2.0, 0.0),
vec2(1.41421356237, 1.41421356237),
vec2(0.0, 2.0),
vec2(-1.41421356237, 1.41421356237),
vec2(-2.0, 0.0),
vec2(-1.41421356237, -1.41421356237),
vec2(0.0, -2.0),
vec2(1.41421356237, -1.41421356237));

float k = 10.0;
float speed = 4.0;
float pi = 3.14159265359;
float startTime = 0.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // (displacement, velocity, obstacle, 1.0);
    vec2 uv = fragCoord.xy;
    vec2 p1 = iMouse.xy;
    vec2 p3 = vec2((iTime-startTime)*32.0*1.5 + 100.0, iResolution.y * 0.75);
    vec2 p4 = vec2((iTime-startTime)*32.0 + 100.0, iResolution.y * 0.5);
    vec2 p5 = vec2((iTime-startTime)*32.0*0.75 + 100.0, iResolution.y * 0.25);
    vec2 p2 = texture(iChannel1, vec2(0.25, 0.5)).zw;
    vec2 mp = texture(iChannel1, vec2(0.75, 0.5)).xy;

    float d = lDistance(uv, p1, p2);
    // pulsed mouse dragging:    (remove this â†“ semicolon and two slashes.)
    // brush radius:    â†“
    // brush smoothness:â†“     â†“
    // brush strength:  â†“     â†“          â†“
    d = clamp(1.0-pow(d/20.0, 5.0), 0.0, 1.0);// * (sign(sin(float(iFrame)*pi+pi/2.0)-0.9)*0.5 + 0.5);

    if (mp.x <= 0.0 || mp.y <= 0.0 ) d=0.0;

    // speed of sound demos: (uncomment the three lines below)
    d += clamp(1.0-distance(uv, p3)/10.0, 0.0, 1.0) * (sign(sin(float(iFrame)*0.1*pi+pi/2.0)-0.9)*0.5 + 0.5);
    d += clamp(1.0-distance(uv, p4)/10.0, 0.0, 1.0) * (sign(sin(float(iFrame)*0.1*pi+pi/2.0)-0.9)*0.5 + 0.5);
    d += clamp(1.0-distance(uv, p5)/10.0, 0.0, 1.0) * (sign(sin(float(iFrame)*0.1*pi+pi/2.0)-0.9)*0.5 + 0.5);

    vec4 old = texture(iChannel0, uv/iResolution.xy);

    float isO = texture(iChannel3, vec2(79.0/255.0, 1.0)).x;
    float isR = texture(iChannel3, vec2(82.0/255.0, 0.0)).x;
    float isS = texture(iChannel3, vec2(83.0/255.0, 0.0)).x;
    float isT = texture(iChannel3, vec2(84.0/255.0, 0.0)).x;
    float v = old.y;
    float o = old.z;
    float h = old.w;
    float dT = clamp(iTimeDelta, 0.003, 0.03);
    vec2 iuv = uv / iResolution.xy;

    if (isR > 0.5) {
        // Velocity based on tension demo:
        if (iuv.x * 0.25 + iuv.y > 0.3) o = 0.7;
        else o = 0.3;

        // Refraction demo:
        if (iuv.x + iuv.y * 0.5 > 0.7) o = 0.7;
        else o = 0.0;

        // Load texture from iChannel2:
        o = length(texture(iChannel2, iuv+vec2(0.5,0.0)).xyz);
        if (o < 0.9) {
             o = 0.0;
        } else {
             o = 1.0;
        }

        // Reset:
        o = 0.0;
    }
    if (isT > 0.5) {
        h = (length(texture(iChannel2, iuv+vec2(0.5, 0.0)).xyz));
        //float t = 0.95;
        //if (h < t) {
        //    d = t - h;
        //}
    }

    if (isO > 0.5) {
        o = max(o, d);
        d = 0.0;
    }
    d += old.x;

    // Left-to-right waves:
    //if (fragCoord.x < 2.0/* && iTime < pi/5.0*/) d = sin(iTime*5.0)*5.0 + 5.0;

    for (int i = 0; i < 16; i++) {
        vec2 ds = dss[i];
        float dds = length(ds);
        vec2 s = ds + uv;
        float value = texture(iChannel0, s/iResolution.xy).x;
        float delta = value - old.x;

        delta /= dds;

        float xo = texture(iChannel0, s/iResolution.xy).z;
        float xh = texture(iChannel0, s/iResolution.xy).w;
        if (h > abs(d)) xo += (h - abs(d)) * 2.0;
        v += delta * dT * k * speed * (1.0-clamp(xo, 0.0, 1.0));
    }
    /*if (abs(v) > 50.0) {
        v = 50.0 * sign(v);
        o = 0.0;
    }*/

    d += v * dT * speed;
    //if (o > 0.9999) {
    //    d = 0.0;
    //    v = 0.0;
    //}
    //if (d < h) {
    //    v = 0.0;
    //}

    if (isS > 0.5) {
        d = 0.0;
        v = 0.0;

        startTime = iTime;
    }

    fragColor = vec4(d, v, o, h);
}

// This is for reading mouse input.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (fragCoord.x/iResolution.x < 0.5) {
        float x = iMouse.z * 2.0 - 1.0;
        fragColor = vec4(iMouse.xy, texture(iChannel0, vec2(0.25, 0.5)).xy);
    } else {
        fragColor = vec4(iMouse.z, texture(iChannel0, vec2(0.75, 0.5)).xyz);
    }
}
