vec3 palette(float t) {
    vec3 a = vec3(0.027,0.494,0.690);
    vec3 b = vec3(0.333,0.169,0.263);
    vec3 c = vec3(0.310,0.012,0.216);
    vec3 d = vec3(0.416,0.165,0.165);

    return a + b*cos(6.28318*(c*t-d) );
}

float vignette(vec2 uv, float radius, float softness) {
    float d = length(uv);
    float v = smoothstep(radius, radius - softness, d);
    return v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;

    vec3 finalColor = vec3(0.0);

        float d = length(uv);
        vec3 col = palette(iTime / 3.0);
        d = sin(d * 3.0 + (iTime) / 4.0) / 8.0;
        d = abs(d);
        d = 0.09 / d;
        finalColor += col / d;



    // Apply vignette
    float vig = vignette(uv, 0.95, 0.85);  // Adjust radius and softness as needed
    finalColor *= vec3(vig);

    fragColor = vec4(finalColor, 1.0);
}
