mat2 rot(float a) {
  float ca=cos(a);
  float sa=sin(a);
  return mat2(ca,sa,-sa,ca);
}



float cylcap( vec3 p, float h, float r )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h,r);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}


float hash11(float t) {
  return fract( sin(t * 7361.994) * 4518.442);
}

vec3 lattice (float t ) {



  float m = t;
  float mx = floor((m-2.)/3.);
  float my = floor((m-1.)/3.);
  float mz = floor((m-0.)/3.);

  float n= m+1.;
  float nx = floor((n-2.)/3.);
  float ny = floor((n-1.)/3.);
  float nz = floor((n-0.)/3.);


  vec3 a =  
  mix(
      vec3(
          hash11(mx)-.5,
          hash11(my)-.5,
          hash11(mz)-.5
      ),
      vec3(
          hash11(nx)-.5,
          hash11(ny)-.5,
          hash11(nz)-.5
      ) , 
      fract(t)
  );
  return a;
}



float hash31(vec3 p3)
{
	p3  = fract(p3 * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}
float noise(vec3 p) {
  vec3 ip=floor(p);
  p=fract(p);
  p=smoothstep(0.0,1.0,p);
  vec3 st=vec3(7,37,289);
  vec4 pos=dot(ip,st) + vec4(0,st.y,st.z,st.y+st.z);
  vec4 val=mix(fract(sin(pos)*7894.552), fract(sin(pos+st.x)*7894.552), p.x);
  vec2 val2=mix(val.xz,val.yw, p.y);
  return mix(val2.x,val2.y, p.z);
}

float fractal(vec3 p) {

  float d=0.5;
  float f=0.0;
  for(int i=0; i<5; ++i) {
    f+=noise(p/d)*d;
    d*=.5;
  }
  return f;
}

float hash21(vec2 p2)
{
	p2  = fract(p2 * .1031);
    p2 += dot(p2, p2.xy + 33.33);
    return fract((p2.x + p2.y));
}

float closestWall(vec3 p) {
    vec3 d = min(fract(p), 1.0 - fract(p));
    return min(min(d.x, d.y), d.z);
}

float box (vec3 p, vec3 s) {
