float map (vec3 p){
    // geo

    vec3 q = p;
    p = mod(p,20.) - 10.;
    float pos = box(p,vec3(5));


    for (float i = 1.; i < 10.; i++) {

            pos = max(pos, -field(q,pow(1.3,i)/16.));

    }

    return pos;
}

vec3 norm(vec3 p) {
  vec2 off=vec2(0.01,0.0);
  return normalize(map(p)-vec3(map(p-off.xyy), map(p-off.yxy), map(p-off.yyx))+0.000001);
}

float tick(float t){
    float i = floor(t);
    float f = fract(t);
    f = smoothstep(0.,1.,f);
    return i + f;
}

float getao(vec3 p, vec3 n, float dist) {
  return clamp(map(p+n*dist)/dist,0.0,1.0);
}
float path (float t) {
    float a = sin(sin(t) + .3 * t);
    return a;

}
float pathfbm(float t) {
    float a = 0.;

    a += path( (t+0. ) );
    a += path( (t+21.)*2.)/2.1;
    a += path( (t+211.)*5.)/5.1;

    return a/2.;
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);


  float tt = iTime*.3;

  // nav


  vec3 s = vec3(0);
  vec3 arm = vec3(0,0,1);
  arm.xz *= rot(cos(tick(tt)));
  arm.zy *= rot(cos(tick(tt*.6)));
  vec3 fwd = lattice(tick(tt))*400.;
  s += fwd;
  vec3 t = s + arm;








  vec3 cz=normalize(t-s);
  vec3 cx=normalize(cross(cz,vec3(0,1,0)));
  vec3 cy=-normalize(cross(cz,cx));
  r=normalize(cx*uv.x+cy*uv.y+cz);


  vec3 p=s;
  float i=0.;
  float dd = 0.;
  float d;

  // march

  bool hit = false;
  for(float z=0.; z <200.; ++z) {
    i = z;
    d=abs(map(p)*.7);
    if(d<0.001) {
      hit = true;
      break;
    }
    if ( dd > 500.) break;
    dd += d;
    p+=r*d;
  }


  // color

  vec3 n = norm(p);
  vec3 light = normalize(vec3(-1,2,3));




  float diff = dot(n,light)*.5+.5;




  vec3 col = vec3(0);

  col += diff;



  if ( ! hit ) {

    col = vec3(1) - pow(r.y,.2);
  }





  fragColor = vec4(col, 1);
}
