// see also https://www.shadertoy.com/view/4csfDl

#define hue(v)  ( .6 + .6 * cos( 6.283*(v)  + vec4(0,23,21,0)  ) )

// Fork of "Integer Hash11" by iq. https://shadertoy.com/view/llGSzw
uint hash11( uint n )  // integer hash copied from Hugo Elias
{// n = uint(floatBitsToUint(float(n))); // to shuffle low values
    n = (n << 13U) ^ n;
    n = n * (n * n * 15731U + 789221U) + 1376312589U;
    return n & uint(0x7fffffffU);
 // return float( r & uint(0x7fffffffU))/float(0x7fffffff);
}


void mainImage( out vec4 O,  vec2 u )
{
    vec2 R = iResolution.xy,
         U = u*16./R.y;                                   // 16x16 cells

    uvec2 p = uvec2(U);    // â†“ not better with prime 17U
    uint  r = hash11( p.x + 1920U*p.y );                  // integer hash
    int   b = length(iMouse.xy)>10. ? int(28.*iMouse.x/R.x) : (iFrame/60+16) % 28;
    float c;
    c = float( (r>>b) & 15U ) / 16. ;                     // extract 4 bits
 // c = float( r & uint(0x7fffffffU))/float(0x7fffffff);  // debug
	O = hue( c );
 // O = vec4( c );

    if (U.x > 16.) O*=0.;
    O += smoothstep(1.5/R.y,0.,length(U/16.-.02 - vec2(1.4,float(b)/28.) )-.01); // show set
}
