void mainImage( out vec4 O, vec2 U ) {
    float H = iResolution.y;
    O -= O;
    U *= 5./H; 
    U.y = U.y * .87 + .5*floor(U.x);
    U = 6.*fract(U)-3.; 
    float x = U.x, 
          a = sign(abs(x) - 1.), 
          b = x>1. && U.y<0. ? 2.: -2.;
    O += smoothstep( 80./H, 0.,
                    abs( a*x +b*U.y + (x<-1.?3.:1.) ) - (x<1.?4.:2.) )
       * (6.-a*b)/8.; 
}

