void mainImage( out vec4 O, vec2 U ) {
    vec2 R = iResolution.xy;
    O -= O;
    U *= 5./R.y; 
    U.y = U.y * .866 + .5*floor(U.x);
    U = 6.*fract(U)-3.;
    bool a = abs(U.x) < 1., b = U.x>1. && U.y<0.;
    O += smoothstep( 80./R.y, 0.,
                    abs( dot( U, vec2( a?-1:1, b?2:-2) ) + (U.x<-1.?3.:1.) ) - (U.x<1.?4.:2.) );
    a || b ? O *= .5 : O; 
}

