void mainImage( out vec4 O, vec2 U ) {
    vec2 R = iResolution.xy;
    O -= O;
    U *= 5./R.y; 
    U.y = U.y * .866 + .5*floor(U.x);
    U = 3.*fract(U)-1.5;
    O +=      U.x  < -.5  ?     S( 1,-2,1.5,  2.)
        : abs(U.x) <  .5  ? .5* S(-1,-2, .5,  2.)
        : U.x>.5 && U.y>0.?     S( 1,-2, .5,  1.)
                          : .5* S( 1, 2, .5,  1.);
}

