#define S(v,r) smoothstep(40./R.y,0.,abs(v) - r)

void mainImage( out vec4 O, vec2 U ) {
    vec2 R = iResolution.xy;
    O -= O;
    U *= 5./R.y; U.y *= .866;
    U.y += .5*floor(U.x);
    U = 3.*fract(U)-1.5;
    float x = U.x;
    if (    x  < -.5  ) O +=     S( U.x+1.5-U.y/.5,  2.);
    if (abs(x) <  .5  ) O += .5* S( .5-U.x -U.y/.5,  2.);
    if (x>.5 && U.y>0.) O +=     S( U.x+.5 -U.y/.5,  1.);
    if (x>.5 && U.y<0.) O += .5* S(  U.x+.5+U.y/.5,  1.);
}

