// Tuned via XShade (http://www.funparadigm.com/xshade/)

vec4 df(vec3 p)
{
    vec2 g = p.xz * 0.2;
    vec4 tex = texture(iChannel1, g + sin(g*1e2 + vec2(1.6,1))/1e2 + iTime*.1);
    float disp = dot(tex.rgb, vec3(0.05));
	float l = length(p) - 4.;
    return vec4(l - disp, tex.rgb);
}

vec3 nor( in vec3 p, vec3 prec )
{
	vec3 e = vec3( 0.03, 0., 0. );
	vec3 n = vec3(
	    df(p+e.xyy).x - df(p-e.xyy).x,
	    df(p+e.yxy).x - df(p-e.yxy).x,
	    df(p+e.yyx).x - df(p-e.yyx).x );
	return normalize(n);
}

vec3 shade(vec3 ro, vec3 rd, float d, vec3 lp, float li)
{
	vec3 p = ro + rd * d;											
	vec3 ld = normalize(lp-p); 										
	vec3 n = nor(p, vec3( 0.001, 0, 0));							
	vec3 refl = reflect(rd,n);																			
	float diff = clamp( dot( n, ld ), 0.0, 1.0 ); 					
	float fre = pow( clamp( 1. + dot(n,rd),0.0,1.0), 4. ); 			
	float spe = pow(clamp( dot( refl, ld ), 0.0, 1.0 ),16.);	
	vec3 tex = df(p).yzw;
	return (diff * 0.4 + fre * 0.1 + spe * 0.3 + tex * .8) * li;					
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0.);

    float time = iTime*0.25;

    float cam_a = time; 

    float cam_e = 5.; 
    float cam_d = 1.2; 

    vec2 uv = (fragCoord * 2.-iResolution.xy)/iResolution.y;

    vec3 ro = vec3(-sin(cam_a)*cam_d, cam_e+1., cos(cam_a)*cam_d); //

  	vec3 cu = vec3(0,1,0);
  	vec3 cv = vec3(0,0,0); 

	vec3 rov = normalize(cv-ro);
    vec3 u = normalize(cross(cu,rov));
  	vec3 v = cross(rov,u);
  	vec3 rd = normalize(rov + uv.x*u + uv.y*v);

	float md = 12.;

    float s = 1., d = 0.;
    for(int i=0;i<150;i++)
    {      
        if (log(d*d/s/1e5)>0.||s>md) break;
        d += s = df(ro+rd*d).x;
    }

    if (d<md)
    {
		col = shade(ro, rd, d, ro, 1.);
    }
    else
    {
        col = texture(iChannel0, rd).rgb;
    }

	fragColor.rgb = col;
}
