// A list of useful distance function to simple primitives, and an example on how to 
// do some interesting boolean operations, repetition and displacement.
//
// More info here: https://iquilezles.org/articles/distfunctions


//------------------------------------------------------------------

float sdPlane( vec3 p )
{
	return p.y;
}

float sdSphere( vec3 p, float s )
{
    return length(p)-s;
}
const float kPi = 3.14159265359;
float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}
//------------------------------------------------------------------

vec2 opU( vec2 d1, vec2 d2 )
{
	return (d1.x<d2.x) ? d1 : d2;
}
float opI( float d1, float d2 )
{
    return max(d1,d2);
}
// Smooth minimum (polynomial smin())
// See https://iquilezles.org/articles/smin
float opBlend( float d1, float d2 ) {
    const float k = 0.1;
    float h = clamp( 0.5+0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h);
}
//------------------------------------------------------------------

vec2 map( in vec3 pos )
{
float d =     
    opBlend(

sdPlane(     pos)   +vec3(0,textureLod(iChannel0,pos.xz*.5,0.).x*.1,0).y, sdSphere(pos-vec3(sin(iTime*.21)*.6+1., sin(iTime)*.15+.15, cos(iTime*.1+sin(iTime*2.2))*.6+1.), 0.25 ));
	d=opBlend(d, sdSphere(pos-vec3(sin(iTime*1.)*.5+1., .01, cos(iTime*1.)*.5+1.), 0.12 ));
	d = opBlend(d, sdSphere(pos-vec3(sin(iTime*1.+kPi)*.5+1., .01, cos(iTime*1.+kPi)*.5+1.), 0.12 ));
    d = opI(d, sdBox(vec3(pos-vec3(1,0,1)), vec3(1)));

vec2 res = vec2(d,50.+sin(iTime*.001)*100.);    
    return res;
}

vec2 castRay( in vec3 ro, in vec3 rd )
{
    float tmin = 1.0;
    float tmax = 20.0;

    float t = tmin;
    float m = -1.0;
    for( int i=0; i<64; i++ )
    {
	    float precis = 0.0005*t;
	    vec2 res = map( ro+rd*t );
        if( res.x<precis || t>tmax ) break;
        t += res.x;
	    m = res.y;
    }

    if( t>tmax ) m=-1.0;
    return vec2( t, m );
}


float calcSoftshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
	float res = 1.0;
    float t = mint;
    for( int i=0; i<16; i++ )
    {
		float h = map( ro + rd*t ).x;
        res = min( res, 8.0*h/t );
        t += clamp( h, 0.02, 0.10 );
        if( h<0.001 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

vec3 calcNormal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773*0.0005;
    return normalize( e.xyy*map( pos + e.xyy ).x + 
					  e.yyx*map( pos + e.yyx ).x + 
					  e.yxy*map( pos + e.yxy ).x + 
					  e.xxx*map( pos + e.xxx ).x );
}

float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.01 + 0.12*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos ).x;
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );    
}

// https://iquilezles.org/articles/checkerfiltering
float checkersGradBox( in vec2 p )
{
    // filter kernel
    vec2 w = fwidth(p) + 0.001;
    // analytical integral (box filter)
    vec2 i = 2.0*(abs(fract((p-0.5*w)*0.5)-0.5)-abs(fract((p+0.5*w)*0.5)-0.5))/w;
    // xor pattern
    return 0.5 - 0.5*i.x*i.y;                  
}

vec3 render( in vec3 ro, in vec3 rd )
{ 
    vec3 col = vec3(0.7, 0.9, 1.0) +rd.y*0.8;
    vec2 res = castRay(ro,rd);
    float t = res.x;
	float m = res.y;
    if( m>-0.5 )
    {
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal( pos );
        vec3 ref = reflect( rd, nor );

        // material        
		col = 0.45 + 0.35*sin( vec3(0.05,0.08,0.10)*(m-1.0) );

        // lighitng        
        float occ = calcAO( pos, nor );
		vec3  lig = normalize( vec3(0.4, 0.7, -0.6) );
        vec3  hal = normalize( lig-rd );
		float amb = clamp( 0.5+0.5*nor.y, 0.0, 1.0 );
        float dif = clamp( dot( nor, lig ), 0.0, 1.0 );
        float bac = clamp( dot( nor, normalize(vec3(-lig.x,0.0,-lig.z))), 0.0, 1.0 )*clamp( 1.0-pos.y,0.0,1.0);
        float dom = smoothstep( -0.1, 0.1, ref.y );
        float fre = pow( clamp(1.0+dot(nor,rd),0.0,1.0), 2.0 );

        dif *= calcSoftshadow( pos, lig, 0.02, 2.5 );
        dom *= calcSoftshadow( pos, ref, 0.02, 2.5 );

		float spe = pow( clamp( dot( nor, hal ), 0.0, 1.0 ),16.0)*
                    dif *
                    (0.04 + 0.96*pow( clamp(1.0+dot(hal,rd),0.0,1.0), 5.0 ));

		vec3 lin = vec3(0.0);
        lin += 1.30*dif*vec3(1.00,0.80,0.55);
        lin += 0.40*amb*vec3(0.40,0.60,1.00)*occ;
        lin += 0.50*dom*vec3(0.40,0.60,1.00)*occ;
        lin += 0.50*bac*vec3(0.25,0.25,0.25)*occ;
        lin += 0.25*fre*vec3(1.00,1.00,1.00)*occ;
		col = col*lin;
		col += 10.00*spe*vec3(1.00,0.90,0.70);

    	col = mix( col, vec3(0.8,0.9,1.0), 1.0-exp( -0.0002*t*t*t ) );
    }

	return vec3( clamp(col,0.0,1.0) );
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 mo = iMouse.xy/iResolution.xy;
	float time = 15.0 + iTime;
time=15.;
    mo=vec2(1.,1.);
    vec3 tot = vec3(0.0);
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;

		// camera	
        vec3 ro = vec3(2.8+cos(iTime*.1)*.1, 0.0 + 0.75, 1.+sin(iTime*.1));
        vec3 ta = vec3( -0.5, -0.4, 0.5 );
        // camera-to-world transformation
        mat3 ca = setCamera( ro, ta, 0.0 );
        // ray direction
        vec3 rd = ca * normalize( vec3(p.xy,2.0) );

        // render	
        vec3 col = render( ro, rd );

		// gamma
        col = pow( col, vec3(0.4545) );

        tot += col;


    fragColor = vec4( tot, 1.0 );
}

const float kPi = 3.14159265359;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 kTexelSize = vec3(1./iResolution.xy, 0.);
	vec2 uv = fragCoord.xy / iResolution.xy;    
	float h = 0.;

    h = texture(iChannel0, uv).x;
    float x = sin(iTime*.21)*.3+.5;
    float y = cos(iTime*.1+sin(iTime*2.2))*.3+.5;

	if (length(uv-vec2(x,y))<.1)
	{
		h+= length(uv-vec2(x,y))*.5;  
	    h*=1.-sin(iTime)*.5;
	}

    x=sin(iTime*1.)*.25+.5;
    y=cos(iTime*1.)*.25+.5;

	if (length(uv-vec2(x,y))<.06)
	{
		h+= length(uv-vec2(x,y))*.75;  
	}
    x=sin(iTime*1.+kPi)*.25+.5;
    y=cos(iTime*1.+kPi)*.25+.5;

	if (length(uv-vec2(x,y))<.06)
	{
		h+= length(uv-vec2(x,y))*.95;  
	}

    h*=mix(1.,mix(texture(iChannel1,uv).x,texture(iChannel2,uv).x,.5),.2);
    vec2 qv = uv * 2. - 1.;
    h=clamp(h,texture(iChannel3,vec2(sin(iTime*.01+length(qv)),cos(iTime*.01+length(uv)))*.5+.5).x*.2,1.);
    h=clamp(h,texture(iChannel3,uv).x*.2,1.);

    fragColor = vec4(h);
}
