float plot(vec2 st, float val, float w) {
    return smoothstep(val-w, val, st.y) - smoothstep(val, val+w, st.y);
}

float wave(vec2 st, float offset)
{
    return 0.9 * sin(st.x * 30. + sin(st.y * 59.096) + sin(st.y * 18.272 + iTime + offset)) * 0.5 + 0.5;
}

float circle(vec2 st, float innerRadius, float width, float edge)
{
    float outerRadius = innerRadius + width;
    float ir2 = innerRadius * innerRadius;
    float or2 = outerRadius * outerRadius;
    float x = st.x - 0.5;
    float y = st.y - 0.5;
    return smoothstep(ir2 - edge, ir2, x * x + y * y) - smoothstep(or2, or2 + edge, x * x + y * y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy / iResolution.xy;
    st.x *= iResolution.x/iResolution.y;
    float wave1 = wave(st, 0.);
    float wave2 = wave(st, 0.132);
    float wave3 = wave(st, 0.364);

    float circ1 = circle(st, (sin( distance(st, vec2(0.5, 0.5))*20. + iTime) * 0.5 + 0.5) * 0.5, 0.404, 0.016);
    float circ2 = circle(st, (sin( distance(st, vec2(0.5, 0.5))*20. + iTime + 0.020) * 0.5 + 0.5) * 0.5, 0.324, 0.016);
    float circ3 = circle(st, (sin( distance(st, vec2(0.5, 0.5))*20. + iTime + 0.042) * 0.5 + 0.5) * 0.5, 0.332, 0.016);

    vec3 color;

    float width = 0.169;
    color = color + vec3(plot(st, wave1, width), 0., 0.);
    color = color + vec3(0., plot(st, wave2, width), 0.);
    color = color + vec3(0., 0., plot(st, wave3, width));

    vec3 t = vec3(circ1, circ2, circ3);
    color =  (1. - color) * t + color * (1. - t);

    fragColor = vec4(color, 1.0);
}
