// @FabriceNeyret2 shorter variant
void mainImage( out vec4 O,  in vec2 fragCoord )
{
    // Normalize
    vec2 R = iResolution.xy;
    vec2 m = iMouse.xy / R;
    vec2 uv = fragCoord/R.y;

    uv += vec2(-0.39,0.0);   // Translate
    uv *= 2.0;              // Resize
    uv = vec2(uv.y, uv.x);  // Rotate 90 degrees

    O = vec4(uv, m.x, 0.0);
    int MaxIter = int(m.y*11.0)+7;

    for (int i = 0; i < MaxIter; i++){
        O.xzy = abs(O.xyz/dot(O,O)-vec3(1.0));
    }
}
