const float is3=1./sqrt(3.);
const mat2 sk=mat2(2.*is3,is3,0.,1.)*5.,unsk=inverse(sk),pl=mat2(1.,0.,-1.,1.);
const vec2 oi=vec2(0.,1.);
float C,S;
#define rot(a) mat2(C=cos(a),S=sin(a),-S,C)

vec3 hsv(vec3 c) {
    vec4 k=vec4(1.,2./3.,1./3.,3.);
    vec3 p=abs(fract(c.xxx+k.xyz)*6.-k.www);
    return c.z*mix(k.xxx,clamp(p-k.xxx,0.,1.),c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv-=.5;
    uv.x*=iResolution.x/iResolution.y;
    uv*=rot(sin(length(uv)*20.-iTime*5.)*.05);
    vec2 skuv=sk*uv;
    vec2 fuv=fract(skuv);
    vec2 iuv=floor(skuv);
    vec2 p0=iuv,p1=iuv+oi.yy,p2;
    if (fuv.y>fuv.x)
        p2=iuv+oi.xy;
    else
        p2=iuv+oi.yx;
    p0=unsk*p0;
    p1=unsk*p1;
    p2=unsk*p2;
    float d0=length(p0-uv);
    float d1=length(p1-uv);
    float d2=length(p2-uv);
    vec2 p=p0;
    if (d1<d0) p=p1;
    if (d2<d0 && d2<d1) p=p2;
    vec2 o=uv-p;
    float a=atan(o.y,o.x)/6.2831*3.;
    o*=rot(floor(a+.5)/3.*6.2831);
    a=mod(a*2.+1.,2.)-1.;
    if (a<0.) o.y=-o.y;
    vec2 plo=pl*o;
    plo*=rot(iTime*2.);
    float h=floor((plo.x*5.+iTime*.3)*5.)/10.,
        s=1.,
        v=1.;
    vec3 c=hsv(vec3(h,s,v));
    c=sqrt(c);
	fragColor = vec4(c,1.);
}
