f4 charmorph(in vec2 U){float t=3.*iTime;
 f4 O=char(U,int(t));// try .xxxx for mask, .wwww for distance field.
 f4 O2=char(U,int(++t));
 O=mix(O,O2,fract(t));             //linear morphing 
 //O = sqrt(mix(O*O,O2*O2,fract(t)));  // quadratic morphing
 O =  smoothstep(.5,.49,O.wwww)
 *O.yzww;// comment for B&W
 //O+=.5;O*=O*O;O-=.5;//cubic contrast;
 return O;}

//isInside returns distance to a square 
//with center offset by offsetXY=.5;
//and with HalfSizeXY=.5;
float isInside(vec2 p, vec2 c){
 p.x*=2.;c.x*=2.;//scaling
 float offsetXY=.0;
 vec2 d=abs(p-offsetXY-c)-.5;
 return-max(d.x,d.y);}
//offsetXY and HalfSizeXY could easily be the same input parameter.
//scaling for IsInside() may be fster if it takes a mat2 as input.

void mainImage2(out f4 O,vec2 p){
 p/=iResolution.y;
 O=.1*texture(iChannel0,p);
 O=max(O,charmorph(p));//morphing char.
}

