//get bit b from given number; Shift right (num / 2^bit), AND the result with 1 (mod(result,2.))
float ebi(float n,float b){b=clamp(b,-1.,24.);return floor(mod(floor(n/pow(2.,floor(b))),2.));}
//Return pixel at v in the given bit-packed sprite.
float sprite(f4 j, vec2 s,vec2 v){v=floor(v);
 if(all(greaterThanEqual(v,vec2(0)))&&all(lessThan(v,s))){//Clipping bound
   float b=(s.x-v.x-1.)+v.y*s.x;//Calculate bit to extract (x+y*width)(flipped on x-axis)
   return ebi(j.x,b-72.)+ebi(j.y,b-48.)+ebi(j.z,b-24.)+ebi(j.w,b);}return 0.;}
