     return 16+i;//show # of word.
    #endif
    #if (debug==2)
     return 16-w;//show # of letters of this word
    #endif
    return wa[(u.x-posInLine-w)+i*wordl];
   }
   posInLine+=1;//add the separator
  }
 }

 //below was older initial concept, it failed to solve Wort-wrapping

 /*

