 //... because the negative lengths indicates a "SPACE" separator after the word.
 //may look ugly for "direct speech  she:" blah bah"., but thats minor aestetics.
 //if(word has positive length, add next word, till one has egative length.)
 int GlyphsPerLine=16;//max glyphs per line. longer lines do NOT eat more memory!
 //int currWord=0;//word number that is currntly being put into a line.
 int currLine=0;//line that we currently set words into.
 int posInLine;//cursor position in currLine;
 int acclen=0;//accumulative length for words without seperator
 for(int i=0;i<words;i++){
  int w=wl[i];//w=word length.
  if(w>-1){//word is NOT followed by a seperator,
   acclen+=wl[i];
  }else{//word is followed by sepertor, test if line breaking is needed
   //and if it fits in the same line, see if we did set a glyph in the tile we care for.
   if(posInLine+acclen-w<GlyphsPerLine){//if(accumulated word fits in line) add to line
    posInLine+=acclen-w+1;//+1 for the "space" seperator.
    wa[i*words-wl[i]]=0;
    //posInLine++;
    if(u.x<=posInLine){
     posInLine+=w-1;//rewind by current word length AND by -1
     u.x-=posInLine;//u.x now points at a glyph within [w]
     return arr2(wa,ivec2(u.x,i))+16;//yeah that should work...
    }

   }else{//this word is too long for this line, needs word wrap
    currLine++;
    posInLine=0;//note that we still keep acclen. 
    //because we may have accumulated a lot of words without seperator.
   // if(u.y<=currLine){
     if(u.x<=posInLine){
      posInLine+=w-1;//rewind by current word length AND by -1
      //repeat that rewind till we get a word with a seperator
      //worst case, no word with separator, so we loop troug hall words?
      int accRew=0;
      for (int j=0;j<words;j++){
       int wwl=wl[i-1-j];if(wl[i-1-j]<0)break;accRew+=wwl;}
      u.x-=posInLine+accRew;//u.x now points at a glyph within [w]
      return arr2(wa,ivec2(u.x,i))+16;;//yeah that should work...
      //but more likely i have a few sign typos here and there.
     }
    }
  // }
  }

 }
