#define BACKGROUND vec3(.007)
float astep(float edge, float x) {
    return clamp((x-edge)/fwidth(x)+.5,0.,1.);
}
float getChar(int char, vec2 p) {
  char=255-char;
  if(p.x>1.||p.y>1.||p.x<0.||p.y<0.) return 0.;
  return 1.-astep(.49,texture(iChannel0, (p+vec2((15-char)%16,char/16))/16.).a);
}
mat4x3 lighten = mat4x3(1. , .1 , .1 ,
                        .1 , 1.2, .1 ,
                        .1 , .1 , 1.5,
                        .05, .05, .05);
mat4x3 burn = mat4x3(.3 , .1 , .1 ,
                     .1 , .3 , .1 ,
                     .1 , .1 , .5 ,
                     -.2, -.2, -.4);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord*2./iResolution.y+vec2(-iResolution.x/iResolution.y,-1.); //-1 to 1, no stretching
    float aspect = iResolution.x/iResolution.y; //above 1 if screen is wider than tall
    vec4 wood = texture(iChannel1, (mat2(1.,-.04,0.,1.)*uv+1.)*.5);
    vec3 col = lighten*wood;
    col = mix(col,col*(1.+dot(uv, vec2(-.4,.4))),astep(.85,length(uv)));
    col = mix(col,BACKGROUND,astep(.9,length(uv)));
    vec3 burnt = burn*wood;
    #if 0 //uppercase
    col = mix(col,burnt,getChar(84,(2.5*(uv-vec2(-.54,-.028))+1.)*.5));
    col = mix(col,burnt,getChar(85,(2.5*(uv-vec2(-.16,-.028))+1.)*.5));
    col = mix(col,burnt,getChar(73,(2.5*(uv-vec2(.19 ,-.028))+1.)*.5));
    col = mix(col,burnt,getChar(84,(2.5*(uv-vec2(.54 ,-.028))+1.)*.5));
    #else //lowercase
    col = mix(col,burnt,getChar(84+32,(2.5*(uv-vec2(-.51,-.01))+1.)*.5));
    col = mix(col,burnt,getChar(85+32,(2.5*(uv-vec2(-.16,-.01))+1.)*.5));
    col = mix(col,burnt,getChar(73+32,(2.5*(uv-vec2(.18 ,-.01))+1.)*.5));
    col = mix(col,burnt,getChar(84+32,(2.5*(uv-vec2(.47 ,-.01))+1.)*.5));
    #endif

    // Output to screen
    fragColor = vec4(pow(col,vec3(1./2.2)),1.0);
}
