////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////////////////
#define pi acos(-1.)
#define deg pi/180.
#define R iResolution.xy
#define ar R.x/R.y
#define time iTime*pi/100.
vec3 cs = vec3(1.,2.,3.);

mat2 r2d(float a) {
    return mat2(cos(a),sin(a),-sin(a),cos(a));
}
vec3 c1(vec2 uv, float tt) {

    vec3 col = vec3(0.);
    uv = uv/dot(uv,uv)/100.;
    //uv.xy *= r2d(time);

    uv += vec2(sin(tt),cos(tt))*0.04;

    //uv += vec2(sin(time),cos(time+0.5))*0.04;
    //uv += sin(length(uv)+time)*0.2;
    uv = uv/dot(uv,uv)/100.;
    //uv = abs(uv);
    vec2 pv = uv;
    uv = fract(uv*8.)-0.5;
    uv = abs(uv)-0.25;
    uv = mix(uv,pv,sin(length(uv)*9.)*0.1+0.95);
    uv = uv/dot(uv,uv)/100.;
    //uv.xy *= r2d(time);

    for (int i=0;i<8;i++) {
        uv += vec2(sin(tt+uv.x*4.),cos(tt+uv.y*4.))*0.01;
    }
    uv = uv/dot(uv,uv)/100.;
    uv = fract(uv*8.);
    uv -= 0.5;
    uv = abs(uv)-0.25;
    float t = tt*10.;
    uv *= r2d(time*20.);
    vec2 cv = vec2(length(uv),atan(uv.x,uv.y));
    col += smoothstep(0.01,0.,length(uv+vec2(0.,0.2)));
    float w = max(abs(cv.x-0.2),cv.y*0.1+0.1);
    col += smoothstep(0.2+cv.y*0.063,0.,w);
    col += sin(cs+w*200.+time*-300.)*0.05;
    col = clamp(col,0.,1.);
    return col;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 tv = uv;
    uv -= 0.5;
    uv.x *= ar;
    vec3 col = vec3(0.);
    float c = length(uv);
    float rt = sin(time/25.)*20.;
    for (int i=0;i<60;i++) {
        rt = sin(time/10.+c*sin(time+float(i))*0.005+float(i)*pi*0.5)*20.;
        col += c1(uv,time+float(i)*-0.002)/19.*(sin(cs*1.1+float(i)+time*20.)*0.8+0.4);
        uv += vec2(sin(uv.y*40.+time),cos(uv.x*40.+time))*0.0004;
        uv *= r2d(rt*1.+sin(rt*100.+c*0.01)/100.);
        //uv = uv/dot(uv,uv);
        uv *= 1.+sin(rt*2.)*0.03;
    }
    //col *= 1.1;
    //col -= 0.2;
    fragColor = vec4(col,1.0);
}
