float ln (vec2 p, vec2 a, vec2 b) {
    return length(p-a-(b-a)*clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.));
}

 #define ei(a) mat2(cos(a),sin(a),-sin(a),cos(a))

 #define cell(u) ((u).x>=-.5&&(u).y>=-.5&&(u).y<.5&&(u).x<.5)
vec4 hash44(vec4 p4)
{
	p4 = fract(p4  * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}
vec4 noise (vec2 U) {
    vec2 f = floor(U);
    vec2 c = ceil(U);
    vec4 _00 = hash44(vec4(f,f));
    vec4 _01 = hash44(vec4(f.x,c.y,f.x,c.y));
    vec4 _10 = hash44(vec4(c.x,f.y,c.x,f.y));
    vec4 _11 = hash44(vec4(c,c));
    vec4 _0 = mix(_00,_01,fract(U.y));
    vec4 _1 = mix(_10,_11,fract(U.y));
    return mix(_0,_1,fract(U.x));
}
vec4 fbm (vec2 U) {
    mat2 m = ei(.5);
    vec4 o = vec4(0);
    for (float i = 0.; i < 10.; i++) {
        U *= 1.5*m;
        o += (noise(U))/5.*exp2(-.5*i);
    }
    return o;
}

