// Unported License.
//
// "truchet hexagonal dodecahedron"
//
// created by Colling Patrik (cyperus) in 2023
//
//
// References & Inspiration:
//
// - quick guide for shadertoy by FabriceNeyret2
//   https://shadertoyunofficial.wordpress.com/
//
// - "Schwarz-Christoffel Mapping" by mla
//   https://www.shadertoy.com/view/tsfyRj
//
// - conformal mapping with confmap.m by Trefethen
//   https://people.maths.ox.ac.uk/trefethen/lightning.html
//
// - signed distance functions & operations by iq
//   https://iquilezles.org/articles/distfunctions/
//
////////////////////////////////////////////////////////////////////////////////

// ray casting bounding shape
const float
    CAM_DIST = 7.,
    CAM_FLE = 2.,
    BB_DIST = 3.,
    RAY_MIN_DIST = 0.1,
    RAY_MAX_DIST = CAM_DIST+1.1*BB_DIST;   

const int MAX_MARCHING_STEPS = 600;
const float
    RAY_STEP_FACTOR = 0.0095,
    RAY_PRECISION = 1.E-1;

// data from sdf
vec3 mat_to = vec3(0);
vec3 mat_to_id = vec3(0);
vec4 mat_tc = vec4(0);

float map(in vec3 p) {
    float rxy = 0., au = 0.0, av = 0.0;
    vec2 z = vec2(0.);
    float d = -1.;

    /// map platonic solid cells to one cell
    vec3 pn = normalize(p); 

    // projection from n cells to 1 cell.
    float cell_id = 0., sr = 0.; //sig(sr): sens of rotation, abs(sr) : number edges per face
    pn = dodeca_cells(pn, cell_id, sr);

    z = pn.xy/(1.-pn.z);
    z = cDodecaPolyArc2disk(z);

    // r-polygon-distribution
    vec2 e = vec2(sqrt(2.), 0.5);
    float en = 1.+( (8.<0.?e.x:e.y)-1. )*smoothstep(1.,5., abs(8.));        
    float r = length(z); vec2 zn = z/r;
    z = dist(r, en) * zn;   

    // stereograpic projection    
    pn = sp(vec3(z,sr));

    // scale radius
    p = length(p) * pn;

    p = p.yzx;

    /// torus
    z = vec2(length(p.zx), p.y);
    au = atan(p.x, p.z);

    z = cmul(z,z); z.x -= 6.;
    av = atan(z.y, z.x);
    rxy = length(z);

    vec2 grid_hex_num = 10. * vec2(3.,1.);
    p = vec3(grid_hex_num.x*au/PI, grid_hex_num.y* 0.5*sqrt(3.)* av/PI, log(rxy)-0.4);

    /// regular hexagonal tiles
    vec3 rpt_id = vec3(1.); vec3 p_hex;    
    p.xy = hexgrid2hex(p.xy, rpt_id);    
    p.xy = inversesc(p.xy, int(6.));
    mat_to_id = rpt_id;

    // torus,sphere,2spheres       
    rxy = length(p.xy); au = atan(p.y, p.x);    
    z = 2.*ccos(PI*vec2(rxy,2.*p.z));
    z.x += (3. + 2. * sin(2.*PI*iTime/60.));

    p = vec3(log(length(z))-0.02, 0.5*au/PI, 0.5*atan(z.y,z.x)/PI);

    vec2 to_uv = p.yz;
    float to_d = log(length(z)+0.8);
    mat_to = vec3(to_uv,to_d);   

    /// truchet 3D cube grid
    vec3 tc_id = vec3(0.); float tc_a = 0.;   
    p *= vec3(1.,18.,4.); // r,u,v
    p.yzx = p.xyz;

    ivec3 l0 = -ivec3(2.,0,9.);
    ivec3 l1 = ivec3(2.,1.,9.)-ivec3(1.);
    tc_id = floor(p);
    tc_id = clamp(tc_id, vec3(l0), vec3(l1));
    vec3 q = p - tc_id -0.5;

    // truchet 3D cube tiles
    float rnd = hash21(tc_id.xz);
    if(rnd<.5) q.x = -q.x;
    if(fract(rnd*77.57+iTime/6.)>.75) {
       vec3 q2 =  length(q.xz-.5)<length(q.xz+.5)
                ? vec3(q.xz-.5,0.)  
                : vec3(q.xz+.5,1.);
       d = sdTorus( vec3(q2.x, q.y, q2.y) , vec2(0.5,0.125) );
    }
    else {
       d = opSmoothUnion( sdCylinder( q.zxy, 0.125 ), sdCylinder( q.xzy, 0.125 ), 0.05 );
    }
    mat_tc = vec4(q,rnd);  

    // return
    return min(d,to_d);
}

// intersect
float intersect(in vec3 ro, in vec3 rd, in float px) {
    float res = -1.0;

    // bb
    vec2 dis = isphere( ro, rd , abs(BB_DIST));    
    if( dis.y<0.0 ) return -1.0;
    dis.x = max( dis.x, RAY_MIN_DIST );
    dis.y = min( dis.y, RAY_MAX_DIST );

    // raymarch signed distance field    
	float fh = RAY_STEP_FACTOR;
    float t = dis.x;
    for( int i=0; i<MAX_MARCHING_STEPS; i++  ) {
        vec3 pos = ro + rd*t;
        float th = RAY_PRECISION * px * t;
        float h = map( pos);
        if( t>dis.y || h<th ) break;
        t += h*fh;
    }
    if( t<dis.y ) {
        res = t;
    }
    return res;
}

vec3 calcNormal( in vec3 p, in float prec ) {
    vec2 e = vec2( prec, 0. );
    return normalize(vec3(
		map(p+e.xyy) - map(p-e.xyy),
		map(p+e.yxy) - map(p-e.yxy),
		map(p+e.yyx) - map(p-e.yyx) )
    	);
}

vec3 transform(in vec3 p, in float time) { 
	if (iMouse.x > 0.0) {
        vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
		p.yz = crot(p.yz,-PI*m.y);
		p.zx = crot(p.zx,-PI*m.x);
	}
    //p.yz = crot(p.yz,-1.*PI*(time+10.));
    //p.zx = crot(p.zx,-2.*PI*(time+15.));
	return p.xyz;
}

// textures
float grid_uv(in vec2 uv) {
    // uv grid alpha mask
    vec2 sw = abs(fract(uv-0.25)-0.5)-0.25;
    float sdf = min(sw.x,sw.y)*-max(sw.x,sw.y);
    float pp = fwidth(sdf);
    return smoothstep(+pp,-pp,sdf);
}

vec4 cells(in vec2 uv, in float h) {
    // sectors, uv-grid
    vec4 C;
    C.a = 1.;
    C.rgb = h2rgb(h);                      // sector  -> hue
    C.rgb *= (0.5+ 0.5 * grid_uv(uv))*0.7; // uv-grid -> value
    return C;
}

vec4 ribbon(in float uv_x, in float time) {
    float sdf = -(2.*pulsecollision(1.,1./3. * uv_x, time, 1./3.)-1.);
    float px = fwidth(sdf); float a = smoothstep(px+0.1,-px-0.1,sdf+0.2);    
    return vec4( h2rgb(sdf -2./6.),a );
}

vec4 truchet3D(in vec3 p, in float rnd ,in float time) {
    float sdf = sdBox( p, vec3(0.5*0.9));
    float px = fwidth(sdf); float a = smoothstep(px,-px,sdf);
    return mix(vec4(1.), vec4(h2rgb( fract(abs(rnd)*22.93 + time)),1.), a);
}

// main
void mainImage(out vec4 O, in vec2 fragCoord) {
    // normalized time
    float ntime = iTime/360.;

    // anti-aliasing to take a picture
    vec3 tot = vec3(0.0);
    #define AA 1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {       
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 uv = (2.0*(fragCoord+o) - iResolution.xy)/iResolution.y;

        // camera viewport --> worldspace
        vec3 ro = vec3(0,0,-CAM_DIST);
        vec3 rd = vec3(-uv.x,uv.y,CAM_FLE);
             ro = transform(ro,ntime);
             rd = normalize(transform(rd,ntime));

        // distance hit ray surface
        float px = 2.0/( iResolution.y*CAM_FLE );
        float t = intersect( ro, rd, px );

        // background
        vec3 sky_col = vec3(0.);
        vec4 C = vec4(sky_col,1.);
        O = C;

        // object surface
        if (t >= 0.) {
            // surface properties
            vec3 p = ro + rd * t;
            vec3 n = calcNormal( p, 0.001);

            // draw textures
            O = vec4(0);

            if(O.a<1.) {          
                float hue = (mat_to_id.x + 2.)/4. + (mat_to_id.z )/8.;
                C = cells(mat_to.xy/2., hue);
                C.a = mat_to.z>0.5 ? 0.5 : O.a;
                O += (1.-O.a) * vec4( C.rgb, 1 ) *C.a;
            }

            if(O.a<1.) {
                C =  mat_to.z<0.5 ? ribbon( 6.*3.* mat_to.x,  6.*ntime) : vec4(0.);
                C.rgb *= 0.7;
                O += (1.-O.a) * vec4( C.rgb, 1 ) *C.a;
            }

            if(O.a<1.) {
                C = truchet3D(mat_tc.xyz, mat_tc.w, ntime);
                C.a = 0.5; C.rgb *= 0.5;
                O += (1.-O.a) * vec4( C.rgb, 1 ) *C.a;
            }

            C = O;

            // lighting
            vec3  came_li_o   = transform(vec3(-10.,10.,-10.),ntime); // origine
            vec3  came_li_dir = normalize(p - came_li_o);             // direction
            vec3  came_li_col = vec3(1.,1.,.5);                       // color
            float came_li_shi = 10.;                                  // shininess            

            float cosa = 1.;
            O.rgb += 0.1 * cosa * C.rgb * sky_col;           // amb
            cosa = clamp(dot(-came_li_dir, n), 0., 1.);
            O.rgb += 0.5 * cosa * C.rgb * came_li_col;       // dif       
            cosa = pow(clamp(dot(reflect(came_li_dir, n), -rd), 0., 1.), came_li_shi);           
            O.rgb += 0.4 *(cosa>0.? cosa:0.) * came_li_col ; // spec

            // fog
            O.rgb = mix(O.rgb, sky_col, 1.0 - exp(-2.5E-3 * pow(t,3.)));    
        }
        tot += O.rgb;
    }
    tot /= float(AA*AA);

    // gamma correction
    O = vec4(rgb2srgb(tot),1.);
}

// const
const float PI = abs(atan(0.,-1.));

// 1D transformations
float pulsecollision(in float pf, in float x, in float x0, in float pw) {
    float ntriw_p = abs( fract( pf*(x - x0) )-0.5);
    float ntriw_n = abs( fract( pf*(x + x0) )-0.5);
    float pp = smoothstep( 0.5*pw, 0., ntriw_p );
    float pn = smoothstep( 0.5*pw, 0., ntriw_n );
    return (pp+pn);
}

float hash21(vec2 p) {
    return fract(sin(dot(p, vec2(27.609, 57.583)))*43758.5453);
}

// complex
vec2 cmul(vec2 za,vec2 zb) {
    return za*mat2(zb.x,-zb.y,zb.yx);
}

vec2 crot(vec2 z,float a) {
    return cmul(vec2(cos(a),sin(a)),z);
}

vec2 cinv(vec2 z) {
    return z*vec2(1,-1)/dot(z,z);
}

vec2 cdiv(vec2 z, vec2 w) {
    return cmul(z,cinv(w));
}

vec2 cpow(vec2 z, int n) {
  float r = length(z);
  float theta = atan(z.y,z.x);
  return pow(r,float(n))*normalize(vec2(cos(float(n)*theta),sin(float(n)*theta)));
}

vec2 csqr(vec2 z) {
    return vec2(z.x * z.x - z.y * z.y, 2. * z.x * z.y );
}

vec2 ccos(vec2 z) {
    vec2 d=vec2(exp(z.y),exp(-z.y));
	return vec2(cos(z.x)*(d.x+d.y)*.5,-sin(z.x)*(d.x-d.y)*.5);
}

float binomial(float a, int n) {
   float s = 1.0;
   for (int i = n; i >= 1; i--,a--) {
     s *= float(a)/float(i);
   }
   return s;
}

vec2 expi(float x) {
  return vec2(cos(x),sin(x));
}

float gamma(float z) {
  const float[8] p = float[](
    676.5203681218851,
    -1259.1392167224028,
    771.32342877765313,
    -176.61502916214059,
    12.507343278686905,
    -0.13857109526572012,
    9.9843695780195716e-6,
    1.5056327351493116e-7
  );
  z -= 1.0;
  float x = 0.99999999999980993;
  for (int i = 0; i < 8; i++) {
    float pval = p[i];
    x += pval/(z+float(i+1));
  }
  float t = z + 8.0 - 0.5;
  return sqrt(2.0*PI) * pow(t,z+0.5) * exp(-t) * x;
}

// beta function
float B(float a, float b) {
  return (gamma(a)*gamma(b))/gamma(a+b);
}

vec2 inversesc(vec2 z, int K) {
  float cn[6];
  for (int n = 1; n <= 5; n++) {
    cn[n] = binomial(float(n)-1.0+2.0/float(K),n)/float(1+n*K);
  }
  float C = B(1.0/float(K),1.0-2.0/float(K))/float(K);
  z *= C;
  vec2 h = cpow(z,int(K));
  float T1 = -cn[1];
  float T2 = -cn[2]+float(K+1)*pow(cn[1],2.0);
  float T3 = -cn[3]+float(3*K+2)*(cn[1]*cn[2]-float(K+1)/2.0*pow(cn[1],3.0));
  float T4 = -cn[4]+float(2*K+1)*(2.0*cn[1]*cn[3]+pow(cn[2],2.0)-float(4*K+3)*
                                  (pow(cn[1],2.0)*cn[2]-float(K+1)/3.0*pow(cn[1],4.0)));
  float T5 = -cn[5]+float(5*K+2)*(cn[1]*cn[4]+cn[2]*cn[3]+float(5*K+3)*
            (-0.5*pow(cn[1],2.0)*cn[3]-0.5*cn[1]*pow(cn[2],2.0)+float(5*K+4)*
            (pow(cn[1],3.0)*cn[2]/6.0-float(K+1)*pow(cn[1],5.0)/24.0)));
  vec2 X = vec2(1,0)+h/pow(C,float(K));
  vec2 w = cmul(z,vec2(1,0) + T1*h + T2*cpow(h,2) + T3*cpow(h,3) + T4*cpow(h,4) + cdiv(T5*cpow(h,5),X));
  return w;
}

// map spherical platonic solid pyramidal sectors to one sector located at -z-axis.
vec2 hexgrid2hex(in vec2 Z, out vec3 U_id) {
    const float
        SQRT3 = sqrt(3.),
        SQRT_3 = 1./SQRT3;
	const mat2
        M = mat2(SQRT_3,-1.,-SQRT_3,-1.),
        iM = 0.5*mat2(SQRT3,-SQRT3,-1.,-1.);
    Z = crot(Z,PI/6.);
    Z = iM*Z/0.75;
    vec3 U = vec3(Z.x,-Z.x-Z.y,Z.y);
    U_id = 2.*floor(U*0.5+0.5);
    vec3 U_lo = U-U_id;
    vec3 aU_lo = abs(U_lo);
    if (aU_lo.x > aU_lo.y && aU_lo.x > aU_lo.z) {
        U_lo.x = -U_lo.y-U_lo.z;
        U_id.x = -U_id.y-U_id.z;
    }
    if (aU_lo.y > aU_lo.z) {
        U_lo.y = -U_lo.x-U_lo.z;
        U_id.y = -U_id.x-U_id.z;
    }
    else {
        U_lo.z = -U_lo.x-U_lo.y;
        U_id.z = -U_id.x-U_id.y;
    }
    Z = U_lo.rb;
    Z = M*Z*0.75;
    return crot(Z,-PI/6.);
}

// regular circular arc triangle -> unitcircle conformal mapping by using a barycentric approximation
vec2 cDodecaPolyArc2disk(vec2 z) {
    // https://people.maths.ox.ac.uk/trefethen/lightning.html
    const int NUM_j = 25; // poles
    const vec2[] rf = vec2[NUM_j]
    (vec2(-0.8260, - 0.5637)
    ,vec2( 0.8091, + 0.5877)
    ,vec2(-0.3090, - 0.9511)
    ,vec2(-0.3089, - 0.9511)
    ,vec2( 0.8089, - 0.5879)
    ,vec2(-1.0000, + 0.0000)
    ,vec2(-1.0000, + 0.0001)
    ,vec2(-0.3090, + 0.9511)
    ,vec2(-0.3089, + 0.9511)
    ,vec2( 0.9996, - 0.0294)
    ,vec2( 0.9103, + 0.4140)
    ,vec2( 0.2242, + 0.9746)
    ,vec2( 0.3369, - 0.9415)
    ,vec2(-0.8576, + 0.5143)
    ,vec2(-0.4307, - 0.9025)
    ,vec2(-0.3567, + 0.9342)
    ,vec2(-0.9988, - 0.0485)
    ,vec2( 0.7796, + 0.6263)
    ,vec2( 0.7796, - 0.6263)
    ,vec2(-0.2828, - 0.9592)
    ,vec2(-0.3161, + 0.9487)
    ,vec2(-1.0000, + 0.0075)
    ,vec2(-0.2187, + 0.9758)
    ,vec2( 0.8366, - 0.5479)
    ,vec2( 0.8134, + 0.5817));

    const vec2[] rw = vec2[NUM_j]
    (vec2(-0.1213, - 0.0   )
    ,vec2( 0.0028, + 0.0187)
    ,vec2( 0.0000, - 0.0000)
    ,vec2(-0.0010, + 0.0008)
    ,vec2(-0.0017, - 0.0008)
    ,vec2(-0.0000, - 0.0000)
    ,vec2(-0.0041, - 0.0007)
    ,vec2(-0.0000, - 0.0000)
    ,vec2( 0.0219, - 0.0012)
    ,vec2(-0.2767, - 0.2629)
    ,vec2( 0.2548, - 0.3937)
    ,vec2( 0.4733, + 0.0936)
    ,vec2(-0.0954, - 0.0232)
    ,vec2(-0.1703, + 0.0600)
    ,vec2(-0.0347, - 0.0277)
    ,vec2(-0.3617, + 0.0048)
    ,vec2(-0.0493, + 0.0086)
    ,vec2( 0.1573, + 0.1009)
    ,vec2(-0.0406, - 0.0040)
    ,vec2(-0.0118, - 0.0116)
    ,vec2( 0.0485, + 0.1712)
    ,vec2(-0.0014, + 0.0204)
    ,vec2( 0.1498, + 0.3189)
    ,vec2(-0.0419, - 0.0255)
    ,vec2( 0.1042, - 0.0471));

    const vec2[] rz = vec2[NUM_j]
    (vec2(-0.2339, - 0.1614)
    ,vec2( 0.2737, + 0.1982)
    ,vec2(-0.1045, - 0.3217)
    ,vec2(-0.1039, - 0.3216)
    ,vec2( 0.2731, - 0.1991)
    ,vec2(-0.3383, + 0.0   )
    ,vec2(-0.3379, + 0.0006)
    ,vec2(-0.1045, + 0.3217)
    ,vec2(-0.1039, + 0.3216)
    ,vec2( 0.2841, - 0.0069)
    ,vec2( 0.2810, + 0.1078)
    ,vec2( 0.0682, + 0.2764)
    ,vec2( 0.0943, - 0.2680)
    ,vec2(-0.2418, + 0.1502)
    ,vec2(-0.1496, - 0.2698)
    ,vec2(-0.1287, + 0.2944)
    ,vec2(-0.3202, - 0.0306)
    ,vec2( 0.2411, + 0.2130)
    ,vec2( 0.2411, - 0.2130)
    ,vec2(-0.0808, - 0.3165)
    ,vec2(-0.1114, + 0.3141)
    ,vec2(-0.3331, + 0.0088)
    ,vec2(-0.0510, + 0.3095)
    ,vec2( 0.2770, - 0.1635)
    ,vec2( 0.2747, + 0.1886));

    // polynomial barycentric representation
    vec2 zn = vec2(0.); vec2 zd = vec2(0.);       
    for(int j = 0; j<NUM_j; j++)
    {
        vec2 zj = cdiv( rw[j], (z-rz[j]));
        zd += zj; // sum denominator
        zn += cmul(rf[j], zj); // sum nominator
    }
    return cdiv(zn, zd);
}

// map spherical platonic solid pyramidal-face-cells to one cell located at -z-axis.
vec3 dodeca_cells(in vec3 p, out float cell_id, out float sr) {
    const float PI_2 = PI/2.;
	//// dodecaeder
	const float gr0 = (1.+sqrt(5.))/2.; const float gr1 = 1./gr0; const float gr2 = gr0-gr1;
	// cell-wall-face-normals
	bool nx = dot(vec3( 1., 0., 0.),p)>=0.;
	bool ny = dot(vec3( 0., 1., 0.),p)>=0.;
	bool nz = dot(vec3( 0., 0., 1.),p)>=0.;
	//
	bool n0A = dot(vec3( gr2,-gr0, gr1),p)>=0.;
	bool n0B = dot(vec3( gr1, gr2,-gr0),p)>=0.;
	bool n0C = dot(vec3(-gr0, gr1, gr2),p)>=0.;
	//
	bool n1A = dot(vec3(-gr2,-gr0,-gr1),p)>=0.;
	bool n1B = dot(vec3(-gr1, gr2, gr0),p)>=0.;
	bool n1C = dot(vec3( gr0, gr1,-gr2),p)>=0.;
	//
	bool n2A = dot(vec3(-gr2, gr0, gr1),p)>=0.;
	bool n2B = dot(vec3(-gr1,-gr2,-gr0),p)>=0.;
	bool n2C = dot(vec3( gr0,-gr1, gr2),p)>=0.;
	//
	bool n3A = dot(vec3( gr2, gr0,-gr1),p)>=0.;
	bool n3B = dot(vec3( gr1,-gr2, gr0),p)>=0.;
	bool n3C = dot(vec3(-gr0,-gr1,-gr2),p)>=0.;

	// cells
    float a = PI_2-atan((sqrt(5.)-1.)/2.);
    float b = PI/10.; // 18Â°
    float a_x = 0.; float a_y = 0.; float a_z = 0.;
	// ---------------------------------------------------------------
	//Fpxpy
    if(ny && !n2B && n2C && !n0C && n0B)	
	{
		cell_id = 0.;
		a_y = PI_2;
        a_x = a-PI_2;
		a_z = b;
	}
	//Fnxny
	else if(!ny && n2B && !n2C && n0C && !n0B)
	{
		cell_id = 1.;
		a_y = PI_2;
        a_x = a+PI_2;
		a_z = b;
	}
	// ---------------------------------------------------------------
	//Fpxny
	else if(ny && n1B && !n1C && n3C && !n3B)
	{
		cell_id = 2.;
		a_y = -PI_2;
		a_x =  a-PI_2;
		a_z =  b;
	}
	//Fnxpy
	else if(!ny && !n1B && n1C && !n3C && n3B)
	{
		cell_id = 3.;
		a_y = -PI_2;
		a_x =  a+PI_2;
		a_z =  b;
	}
	// ---------------------------------------------------------------
	//Fpypz
	else if(!nz && n3C && !n3A && n0A && !n0C)
	{
		cell_id = 4.;
		a_y =  0.;
        a_x =  a;
		a_z =  b;
	}
	//Fnynz
	else if(nz && !n3C && n3A && !n0A && n0C)
	{
		cell_id = 5.;
		a_y =  0.; 
		a_x =  a+PI;
		a_z =  b;
	}

	// ---------------------------------------------------------------
	//Fpynz
	else if(nz && n2C && !n2A && n1A && !n1C)
	{
		cell_id = 6.;
		a_y =  0.;
        a_x = -a+PI;
		a_z = -b;        
	}
	//Fnypz
	else if(!nz && !n2C && n2A && !n1A && n1C)
	{
		cell_id = 7.;
		a_y =  0.;
        a_x = -a;
		a_z = -b;       
	}
	// ---------------------------------------------------------------
	//Fpzpx
	else if(!nx && n1A && !n1B && n0B && !n0A)
	{
		cell_id = 8.;
		a_y =  a-PI_2;
		a_x =  0.;
		a_z =  0.;
	}
	//Fnznx
	else if( nx && !n1A && n1B && !n0B && n0A)
	{
		cell_id = 9.;
		a_y =  a+PI_2;
		a_x =  0.;
		a_z =  0.;
	}
	// ---------------------------------------------------------------
	//Fpznx
	else if(nx && n3A && !n3B && n2B && !n2A)
	{
		cell_id = 10.;
		a_y = -a+PI_2;
		a_x =  0.;
		a_z =  b+PI_2;
	}
	//Fnzpx
	else if(!nx && !n3A && n3B && !n2B && n2A )
	{
		cell_id = 11.;
		a_y = -a+PI_2;
		a_x =  PI;
		a_z =  b-PI_2;
	}
	// ---------------------------------------------------------------
	// cell orientation
	vec2 ei_y = vec2(cos(a_y),sin(a_y)); p.zx = cmul(p.zx,ei_y); // rotate around y-axis
	vec2 ei_x = vec2(cos(a_x),sin(a_x)); p.yz = cmul(p.yz,ei_x); // rotate around x-axis
	vec2 ei_z = vec2(cos(a_z),sin(a_z)); p.xy = cmul(p.xy,ei_z); // rotate around z-axis
	// non cyclic
	sr = 5.;
    return p;
}

// distribution
float sig(float x) {
    return x < 0. ? -1. : +1.;
}

float dist(float x, float e) {
    // e in [0., +inf]
    return sign(x)*abs( pow(abs(x), abs(e)) );
}

vec2 r_poly_dist(vec2 z, vec2 e, float sh_a) {   
    /// r-polygon-distribution
    float r = length(z); z = (r<0.)? vec2(0) : z/r; 
    float d0 = 0.5 *abs(e.y-e.x);
    float e2 = e.x + d0 + d0 * (2.*smoothstep(-1., 1., -sh_a) - 1.);
    return dist(r, e2) * z;
}   

// stereographic projection
vec3 sp(vec3 zsr){
    vec2 z = zsr.xy; float sr = zsr.z;
    // stereopraphic projection (complex plane to unit sphere)
    float zz = dot(z,z); vec3 pn;
    if (bool(sig(sr))) {// north pole
        pn = vec3(2. * z.x,  2. * z.y, -1. + zz);
        }
    else { // south pol
        z = cinv(z);
        pn = vec3(2. * z.x, -2. * z.y,  1. - zz);
        }
    pn /= 1.+zz;
    return pn;
}

// bb
vec2 isphere(in vec3 ro, in vec3 rd, in float r) {
	float b = dot(ro,rd);
	float c = dot(ro,ro) - r*r;
    float h = b*b - c;   
    if( h<0.0 ) return vec2(-1.0);
    h = sqrt( h );
    return -b + vec2(-h,h);
}

// sdf
float opIntersection( float d1, float d2 ) {
    return max(d1,d2); }

float opSmoothUnion( in float d1, in float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

float sdSphere( vec3 p, float s ) {
  return length(p)-s;
}

float sdBox( vec3 p, vec3 b ) {
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sdCylinder( vec3 p, float r ) {
  return length(p.xz)-r;
}

float sdTorus( vec3 p, vec2 t ) {
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

// color
vec3 h2rgb(float h) {
    vec3 rgb = clamp( abs(mod(h*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
    return rgb*rgb*(3.0-2.0*rgb); // cubic smoothing
}

// srgb <--> rgb (linear)
vec3 rgb2srgb(vec3 col) {
    return pow(col, vec3(1./2.2));
}
