#define PI 3.14159
#define R iResolution

vec3 black=vec3(0.),white=vec3(1.);
vec3 A=vec3(.5),B=vec3(.5),C=vec3(1.),D=vec3(0.1, .3, .9);
vec3 palette(float t){return A+B*cos(2.*PI*(C*t+D));}

float flower(vec2 uv, float c) {
    float d=length(uv);
    d=sin(d*3.+iTime);
    return .02/abs(cos(atan(uv.y,uv.x)*c)-d);
}

mat2 rot(float a){float c=cos(a),s=sin(a);return mat2(c,-s,s,c);}

void mainImage(out vec4 C, in vec2 I) {
    vec2 uv0 = (2.*I-R.xy)/R.y;
    vec3 col=black;
    vec2 uv=uv0;
    for (float j=0.;j<3.;j++){
        //uv=uv*rot(iTime*.3);
        float d=length(uv);
        for (float i=1.;i<=3.;i++) {
            uv *= (1./i);
            float v=flower(uv,i*4.);
            v=pow(v, 2.1);
            col += mix(black, palette(iTime*.4+d), v);
        }
        uv=fract(uv*2.)-.5;
    }
    C = vec4(col,1.0);
}
