vec3 palette(float t) {
    vec3 a = vec3(0.439, 0.420, 0.420);
    vec3 b = vec3(0.322, 0.318, 0.322);
    vec3 c = vec3(0.447, 0.431, 0.443);
    vec3 d = vec3(0.000, 0.000, 0.000);

    return a + b * cos(99.28318 * (c * t + d));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;

    // Adjust the uv.x coordinate to introduce the glitch effect
    uv.x *= iResolution.x / iResolution.y;
    uv.y /= 1.0 - 0.5 * (iResolution.x * iResolution.y);

    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    for (float i = 0.0; i < 100.0; i++) {
        uv = fract(uv * 0.1);
        float d = length(uv);
        vec3 col = palette(iTime * 99999999.0 + i+ length(uv0));
        d = sin(d * 1000000.0 + (iTime) / 4.0) * 1.5;
        d = abs(d);
        d = 0.00009 / d;
        finalColor += col * d;
    }

    fragColor = vec4(finalColor, 1.0);
}
