precision mediump float;

vec4 drawPolygon(vec2 position, float radius, int sides) {
    float angle = atan(position.x, position.y);
    float slice = 3.14 * 2.0 / float(sides);
    float d = step(radius, cos(floor(0.5 + angle / slice) * slice - angle) * length(position));
    vec3 color = vec3(d); // Use 'd' to generate a color
    return vec4(color, 1.0); // Return a vec4 color
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    float aspect = iResolution.x/iResolution.y; //aspect ratio

    vec2 position = (fragCoord.xy / iResolution.xy) * (iResolution.xy/iResolution.y);
    vec2 center = vec2(aspect/2., 0.5); //center of the window

    int sides = 3; // Number of sides in the polygon
    float polyRadius = 0.1; //radius of the polygon
    float speed = 1.; //speed of the wave
    float phase = 3.;
    float radius = length(position - center); // Calculate distance from center
    float wave = sin(radius * 2.0 - iTime * speed + phase); // Calculate wave displacement
    float scale = .1 + wave; // Apply wave displacement to scale

    position = (position - center) * scale; // Apply scale to position

    vec2 repeat = vec2(20.0); // Adjust this value to change the number of repetitions

    vec4 color = drawPolygon(fract(position*repeat), polyRadius, sides); // No need to convert to vec4

    fragColor = color;
}
