#define S(v,s)  smoothstep( -2., 2., abs(o.x-v) * R.y - s )

void mainImage( out vec4 o,vec2 u)  {
    vec2 R = iResolution.xy, 
         U = (u+u-R)/R.y;    
    o = vec4(.4,.2,0,0) / dot(U,U) ;                            // RGBsource / distance, saturating

    if( R.y>300. && abs(U.y)<.3 && U.x>0. ) o *= S( .93, 70. ); // big mask
    if( U.x>0.) o*= S(.93, 20.) ;                               // thin mask
    float m = iMouse.y/R.y; o = (clamp(o,0.,1.)-m) / (1.-m);    // contrast
    if (U.y<0.) o = pow(o,vec4(1./2.2)) ;                       // bottom: sRGB conversion
}
