float house = 1.0;
float windows = 1.0;
float chimney = 1.0;



vec2 housePos = vec2(0.2,0.0);
vec2 dimensions = vec2(0.6,0.35);

vec2 window1Pos = vec2(0.3,0.4);
vec2 window2Pos = vec2(0.6,0.4);

vec2 windowdimensions = vec2(0.1,0.1);

vec2 chimneyPos = vec2(0.7,0.6);

vec2 chimneydimensions = vec2(0.1,0.4);


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 houseColor = vec4(0.9,0.5,1.0,house);
    vec4 windowColor = vec4(0.0,0.5,1.0,windows);
    vec4 chimneyColor = vec4(1.0,0.0,0.0,chimney);
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float aspectRatio = iResolution.y/ iResolution.x;

  	uv.y *= aspectRatio;
    housePos.y *= aspectRatio;
    window1Pos.y *= aspectRatio;
    window2Pos.y *= aspectRatio;
    chimneyPos.y *= aspectRatio;


    float houseX = step(housePos.x, uv.x) - step(dimensions.x + housePos.x, uv.x);
    float houseY = step(housePos.y, uv.y) - step(dimensions.y + housePos.y, uv.y);

    float window1X = step(window1Pos.x, uv.x) - step(windowdimensions.x + window1Pos.x, uv.x);
    float window1Y = step(window1Pos.y, uv.y) - step(windowdimensions.y + window1Pos.y, uv.y);

    float window2X = step(window2Pos.x, uv.x) - step(windowdimensions.x + window2Pos.x, uv.x);
    float window2Y = step(window2Pos.y, uv.y) - step(windowdimensions.y + window2Pos.y, uv.y);

    float chimneyX = step(chimneyPos.x, uv.x) - step(chimneydimensions.x + chimneyPos.x, uv.x);
    float chimneyY = step(chimneyPos.y, uv.y) - step(chimneydimensions.y + chimneyPos.y, uv.y);

    vec4 Layer0 =vec4(0.0,0.0,0.0,1.0);
    vec4 Layer1 =vec4((houseX * houseY) * houseColor);
    vec4 Layer2 =vec4((window1X * window1Y) * windowColor);
    vec4 Layer3 =vec4((window2X * window2Y) * windowColor);
    vec4 Layer4 =vec4((chimneyX * chimneyY) * chimneyColor);
    // Output to screen
    vec4 composite = mix(Layer0, Layer1, Layer1.a);
    composite = mix(composite, Layer2, Layer2.a);
    composite = mix(composite, Layer3, Layer3.a);
    composite = mix(composite, Layer4, Layer4.a);

    fragColor = composite;
}
