/**/ // ---------------------------------------------- 271 chars

void mainImage( out vec4 O, vec2 U )
{
	vec2 R = iResolution.xy, V;
         U = (U+U-R)/R.y * 8.; V=ceil(abs(U));
    O -= O; if (abs(U.x)>6.) return;

    float // l = length(U)/8.,                // L2 continuous warp
          // l = length(ceil(U))/8.,          // L2 distance, odd center
          // l = length(V)/8.,                // L2 distance, even center
             l = max(V.x,V.y)/8.,             // L1 distance
          // t = iTime*max(0.,1.-l/.8);
             t = iTime*exp(-l*3.),
             e = 35./R.y;

    //         tiles              rotating
    U = abs( (2.*fract(U)-1.) * mat2( sin(t+1.57*vec4(1,2,0,1)) ) );

	O += smoothstep(-e,e, max(U.x,U.y)-.7) -O;
    //O += .1/(max(U.x,U.y)-.8);
}

