// return color from temperature 
//http://www.physics.sfasu.edu/astro/color/blackbody.html
//http://www.vendian.org/mncharity/dir3/blackbody/
//http://www.vendian.org/mncharity/dir3/blackbody/UnstableURLs/bbr_color.html
vec3 blackbody(float Temp)
{
	vec3 col = vec3(255.);
    col.x = 56100000. * pow(Temp,(-3. / 2.)) + 148.;
   	col.y = 100.04 * log(Temp) - 623.6;
   	if (Temp > 6500.) col.y = 35200000. * pow(Temp,(-3. / 2.)) + 184.;
   	col.z = 194.18 * log(Temp) - 1448.6;
   	col = clamp(col, 0., 255.)/255.;
    if (Temp < 1000.) col *= Temp/1000.;
   	return col;
}

vec4 voro(vec2 uv) // https://www.shadertoy.com/view/ltK3WD
{
	uv.x = abs(uv.x);

	float r = abs(uv.y);
	uv = vec2(uv.x/uv.y,abs(1./uv.y) + iTime);

	float df = 0.;

	vec2 g = floor(uv);
	vec2 f = fract(uv);
	vec2 p,cp;
	float t = iTime * 0.1;
	float d = 1.,k;;
	for(int x=-2;x<=2;x++)
    for(int y=-2;y<=2;y++)
    {	
        p = vec2(x,y);
		p += .5 + .35*sin( t * 10. + 9. * fract(sin((g+p)*mat2(2,5,5,2)))) - f;
        k = max(abs(p.x)*.866 - p.y*.5, p.y);
        if (k<d)
        {
            df += k/pow(d,3.) * r;
            d = k;
            cp = p;

        }
	}

	vec3 n = vec3(0);

    if ( d == -cp.x*.866 - cp.y*.5) 	n = vec3(1,0,0);
	if ( d == cp.x*.866 - cp.y*.5) 		n = vec3(0,1,0);
	if ( d == cp.y) 					n = vec3(0,0,1);

    return sqrt((texture(iChannel0, -n) 
                 + blackbody(df*400.-200.).rgbb)*d*2.5) * (1. - 1./(1. + r*r*14.));
}

void mainImage( out vec4 fragColor, vec2 fragCoord )
{
	vec2 g = fragCoord.xy;
	vec2 si = iResolution.xy;
	vec2 uv = (g+g-si)/si.y;

	fragColor = voro(uv);
}
