float mask(vec2 uv, float r)
{
    uv -= .5;
    vec2 p = vec2(0., 0.);
    float dx = uv.x - p.x;
    float dy = uv.y - p.y;
    dx *= .75;
    dy *= .15;
    dy += .1;

    return (r * r) / (dx * dx + dy * dy);
}

float random(vec2 uv)
{
 	return fract(sin(dot(vec2(100., 213.), uv)) * 3141.);   
}

float value_noise(vec2 uv)
{
 	vec2 i = floor(uv);
    vec2 f = fract(uv);

    f = f * f * (3. - 2. * f);

    float b = mix(random(i), random(i + vec2(1., 0.)), f.x);
    float t = mix(random(i + vec2(0., 1.)), random(i + vec2(1.)), f.x);

    return mix(b,t,f.y);
}

float noise(vec2 uv)
{
    float n = value_noise(uv);
    n += value_noise(uv * 2.) * 0.5;
    n += value_noise(uv * 4.) * 0.25;
    n += value_noise(uv * 8.) * 0.125;
    n += value_noise(uv * 16.) * 0.0625;

    return n / 1.9375;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 nuv1 = vec2(uv.x * 5., uv.y - iTime * 0.6);
    vec2 nuv2 = vec2(uv.x * 6., uv.y - iTime * 0.4);
    vec2 nuv3 = vec2(uv.x * 8., uv.y - iTime * 0.7);

    vec4 n1 = vec4(1., 0.,0.,1.) * noise(nuv1);
    vec4 n2 = vec4(0., 0., 1., 1.) * noise(nuv2);
    vec4 n3 = vec4(1.,1., 0.,1.) * noise(nuv3);


    // Output to screen
    fragColor = (n1 + n2 + n3 - vec4(.1)) * (mask(uv, .15)- 0.5); 
}
