//##### power #####
#define p -2.
//try with: -10,-14,2,10,-1.9,-1


//##### zoom options #####
#define center vec2(0)
//vec2(.6114385,0)
#define zoom 1.
//iTime*iTime*iTime*10.


//##### palette options #####
#define palette
#define palettefunc vec3(cos(t*2.)/2.+0.5,sin(t*2.)/2.+0.5,1.-cos(t*2.)/2.-0.5)


//##### animation options #####
#define animate
#define animation sin(t)/600.+1.



float animation_(float t){
	#ifdef animate
    return animation;
    #else
    return 1.;
    #endif
}

vec3 palette_(float t){
	#ifdef palette
    t=1.-t;
    t*=10.;
	return palettefunc;
    #else
    return vec3(t);
    #endif
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    const float mi = 256.;

    vec2 mouse = iMouse.xy/iResolution.xy;
    vec2 c = vec2((mouse.x - .5) * 3., 5.*pow(1.5*(mouse.y - .5), 3.));

	if(iMouse.z<0.||iMouse.x+iMouse.y==0.)c = vec2((2.41/17.*(animation_(iTime*1.1)) - .5) * 3., 5.*pow(1.5*(7.2/10.*(animation_(iTime)) - .5), 3.));

    float lim = 8.0;

    vec2 z = fragCoord.xy / iResolution.xy*2.-1.;
	z.x*=iResolution.x/iResolution.y;
	z*=1./(zoom);
    z+=center;


	float i;
    for(i=0.;i<mi;i++){
		float l=sqrt(z.y*z.y+z.x*z.x);
		l=pow(l,p);
		float ang=atan(z.y,z.x);
      	z=vec2(cos(ang*(p))*l,sin(ang*(p))*l)+c;

        if(dot(z,z)>lim)break;
    }

	fragColor = vec4(palette_(i/mi),1.);
}
